import{_ as o}from"./plugin-vue_export-helper-c27b6911.js";import{r as i,o as g,c as s,a as n,b as t,e as a,d as e,w as p,f as h}from"./app-9ec59630.js";const c={},d=t("p",null,[t("strong",null,"鵬灣跨海大橋"),a("，是大鵬灣最美麗最獨特的新地標，橫跨於大鵬灣域出海口之上，是全國首座，也是目前唯一一座可以配合船舶進出而設計的開啟式景觀橋。")],-1),m={href:"http://www.dbnsa.gov.tw/user/Article.aspx?Lang=1&SNo=05003218",target:"_blank",rel:"noopener noreferrer"},_=h('<p>2011年3月正式通車啟用的鵬灣跨海大橋，擁有特殊三向度設計（有別於一般橋樑只有長與寬二個向度），正面看如鵬鳥啣物，側看如船帆揚起，背面似英文字母大A，其造型採單塔非對稱單面複合式斜張橋。全長579公尺，橋面寬30公尺，主塔頂至水面的高度達71公尺，橋面距水面則是17公尺高。活動橋面單向開啟75度仰角，跨距達20公尺。單次上升橋面的時間僅需兩分鐘而已，放下時間亦同。船帆造型的橋樑除了傳遞海洋意象之外，夜間搭配燈光照明更出色顯眼，可比燈塔一樣成為海上船隻最溫暖的指引良伴。</p><h2 id="鵬灣跨海大橋開啟時間" tabindex="-1"><a class="header-anchor" href="#鵬灣跨海大橋開啟時間" aria-hidden="true">#</a> 鵬灣跨海大橋開啟時間</h2><ul><li>夏季時間(3月~9月)：每週六、日及國定假日下午5時開啟</li><li>冬季時間(10月~2月)：每週六、日及國定假日下午4時30分開啟</li></ul><p>走走日期：2015/09/05</p><h2 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h2><p>國道3號南下，至國道3號終點續行大鵬灣國家風景區環灣道路，於鵬灣跨海大橋前下車。</p><h2 id="走走路線" tabindex="-1"><a class="header-anchor" href="#走走路線" aria-hidden="true">#</a> 走走路線</h2><p>→鵬灣跨海大橋<br><img src="https://1013399.github.io/image-2/66/1097436386_l.jpg" alt="IMGP8208.JPG - 屏東東港大鵬灣跨海大橋開橋秀" loading="lazy"></p><p>→導遊先帶我們至橋上參觀<br><img src="https://1013399.github.io/image-2/66/1097437159_l.jpg" alt="IMGP8209.JPG - 屏東東港大鵬灣跨海大橋開橋秀" loading="lazy"></p><p>→再下至橋下右側堤防上欣賞開橋秀<br><img src="https://1013399.github.io/image-2/66/1097437943_l.jpg" alt="IMGP8211.JPG - 屏東東港大鵬灣跨海大橋開橋秀" loading="lazy"></p><p>→04:50 PM開始交通管制，05:00 PM廣播開始倒數，橋面緩緩升起<br><img src="https://1013399.github.io/image-2/66/1097436893_l.jpg" alt="IMGP8212.JPG - 屏東東港大鵬灣跨海大橋開橋秀" loading="lazy"><br><img src="https://1013399.github.io/image-2/66/1097436695_l.jpg" alt="IMGP8213.JPG - 屏東東港大鵬灣跨海大橋開橋秀" loading="lazy"><br><img src="https://1013399.github.io/image-2/66/1097435993_l.jpg" alt="IMGP8214.JPG - 屏東東港大鵬灣跨海大橋開橋秀" loading="lazy"><br><img src="https://1013399.github.io/image-2/66/1097437346_l.jpg" alt="IMGP8215.JPG - 屏東東港大鵬灣跨海大橋開橋秀" loading="lazy"><br><img src="https://1013399.github.io/image-2/66/1097436484_l.jpg" alt="IMGP8216.JPG - 屏東東港大鵬灣跨海大橋開橋秀" loading="lazy"><br><img src="https://1013399.github.io/image-2/66/1097435897_l.jpg" alt="IMGP8217.JPG - 屏東東港大鵬灣跨海大橋開橋秀" loading="lazy"><br><img src="https://1013399.github.io/image-2/66/1097437557_l.jpg" alt="IMGP8218.JPG - 屏東東港大鵬灣跨海大橋開橋秀" loading="lazy"><br><img src="https://1013399.github.io/image-2/66/1097435995_l.jpg" alt="IMGP8219.JPG - 屏東東港大鵬灣跨海大橋開橋秀" loading="lazy"><br><img src="https://1013399.github.io/image-2/66/1097436389_l.jpg" alt="IMGP8220.JPG - 屏東東港大鵬灣跨海大橋開橋秀" loading="lazy"><br><img src="https://1013399.github.io/image-2/66/1097437161_l.jpg" alt="IMGP8221.JPG - 屏東東港大鵬灣跨海大橋開橋秀" loading="lazy"></p><p>→約2分鐘，橋面已全面升起，約停留5分鐘後再緩緩降下<br><img src="https://1013399.github.io/image-2/66/1097435127_l.jpg" alt="IMGP8222.JPG - 屏東東港大鵬灣跨海大橋開橋秀" loading="lazy"></p><p>→集合上車，夜宿中華電信枋山會館，明日將前往雙流森林遊樂區</p><h2 id="下一篇相關文章" tabindex="-1"><a class="header-anchor" href="#下一篇相關文章" aria-hidden="true">#</a> 下一篇相關文章</h2>',14);function b(u,P){const l=i("ExternalLinkIcon"),r=i("RouterLink");return g(),s("div",null,[d,n(" more "),t("p",null,[a("(資料來源："),t("a",m,[a("大鵬灣國家風景區管理處"),e(l)]),a(")")]),_,t("p",null,[e(r,{to:"/posts/post-65-2015-09-11.html"},{default:p(()=>[a("【屏東獅子】雙流森林遊樂區、帽子山步道、帽子山")]),_:1})])])}const I=o(c,[["render",b],["__file","post-66-2015-09-10.html.vue"]]);export{I as default};
