import{_ as l}from"./plugin-vue_export-helper-c27b6911.js";import{r as e,o as g,c as p,a as r,b as t,f as a,d as o,e as s}from"./app-6d4d6805.js";const n={},h=t("p",null,[t("strong",null,"外傘頂洲"),a("又稱「外傘頂沙洲」、「外傘頂汕」，是台灣沿海最大的沙洲，現存面積約100餘公頃，因形狀似傘得名。受到波浪、沿岸流及東北季風影響，每年平均往西南方向漂移60至70公尺，也被稱為「移動的國土」。")],-1),c={href:"http://dongshih.cyhg.gov.tw/gallery/Details.aspx?Parser=16,8,44,,,,,31",target:"_blank",rel:"noopener noreferrer"},m=s('<p>行政劃分上屬雲林縣口湖鄉，不過如今絕大部份位於嘉義縣東石鄉外海約10餘公里處，主要由濁水溪的泥沙沖積而成，是雲嘉南濱海國家風景區的景點之一。近年來由於河川興建攔砂壩，導致沙源減少而逐漸縮小，嘉義沿海也因此逐漸失去屏障。</p><p>走走日期：2015/09/05</p><h2 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h2><p>國道1號南下，於嘉義系統交流道轉台82線快速道路往東石鄉，至台82線終點續行往漁人碼頭下車。</p><h2 id="走走路線" tabindex="-1"><a class="header-anchor" href="#走走路線" aria-hidden="true">#</a> 走走路線</h2><p>→東石漁人碼頭<br><img src="https://1013399.github.io/image-2/67/1097437544_l.jpg" alt="IMGP8183.JPG - 雲林口湖外傘頂洲" loading="lazy"></p><p>→前往乘船處搭船<br><img src="https://1013399.github.io/image-2/67/1097437547_l.jpg" alt="IMGP8184.JPG - 雲林口湖外傘頂洲" loading="lazy"></p><p>→今日搭乘阿水船長的神聖號，港檢人員檢查後立即出發<br><img src="https://1013399.github.io/image-2/67/1097437453_l.jpg" alt="IMGP8185.JPG - 雲林口湖外傘頂洲" loading="lazy"></p><p>→外傘頂洲與嘉義縣海岸之間的水域稱為東石潟湖，是台灣主要蚵仔養殖區之一<br><img src="https://1013399.github.io/image-2/67/1097436687_l.jpg" alt="IMGP8191.JPG - 雲林口湖外傘頂洲" loading="lazy"></p><p>→此處為固定式的「立棚」蚵架，蚵串垂立於水面上<br><img src="https://1013399.github.io/image-2/67/1097436375_l.jpg" alt="IMGP8192.JPG - 雲林口湖外傘頂洲" loading="lazy"></p><p>→這些則是不受潮汐影響的「浮棚式」蚵架，棚架上旗桿作為識別用<br><img src="https://1013399.github.io/image-2/67/1097435984_l.jpg" alt="IMGP8193.JPG - 雲林口湖外傘頂洲" loading="lazy"></p><p>→看見外傘頂洲了，大部分船都在南岸靠岸，今天船長則帶我們去北岸<br><img src="https://1013399.github.io/image-2/67/1097435206_l.jpg" alt="IMGP8195.JPG - 雲林口湖外傘頂洲" loading="lazy"></p><p>→船行約1小時，抵達北岸下船，必須搶灘涉水上岸<br><img src="https://1013399.github.io/image-2/67/1097435208_l.jpg" alt="IMGP8196.JPG - 雲林口湖外傘頂洲" loading="lazy"></p><p>→赤足走在沙灘上，尋找蛤粒與螃蟹<br><img src="https://1013399.github.io/image-2/67/1097435209_l.jpg" alt="IMGP8198.JPG - 雲林口湖外傘頂洲" loading="lazy"></p><p>→海流形成波紋的外傘頂洲沙灘<br><img src="https://1013399.github.io/image-2/67/1097436882_l.jpg" alt="IMGP8202.JPG - 雲林口湖外傘頂洲" loading="lazy"></p><p>→到處可見行動迅速敏捷的螃蟹<br><img src="https://1013399.github.io/image-2/67/1097435122_l.jpg" alt="IMGP8203.JPG - 雲林口湖外傘頂洲" loading="lazy"><br><img src="https://1013399.github.io/image-2/67/1097435012_l.jpg" alt="IMGP8204.JPG - 雲林口湖外傘頂洲" loading="lazy"></p><p>→走到沙洲盡頭後折返<br><img src="https://1013399.github.io/image-2/67/1097434704_l.jpg" alt="IMGP8205.JPG - 雲林口湖外傘頂洲" loading="lazy"></p><p>→回到船上享用午餐，導遊帶頭歡唱熱舞<br><img src="https://1013399.github.io/image-2/67/1097435613_l.jpg" alt="IMGP8206.JPG - 雲林口湖外傘頂洲" loading="lazy"></p><p>→回到東石漁人碼頭<br><img src="https://1013399.github.io/image-2/67/1097436785_l.jpg" alt="IMGP8207.JPG - 雲林口湖外傘頂洲" loading="lazy"></p>',19);function d(_,b){const i=e("ExternalLinkIcon");return g(),p("div",null,[h,r(" more "),t("p",null,[a("(資料來源："),t("a",c,[a("嘉義縣東石鄉公所"),o(i)]),a(")")]),m])}const u=l(n,[["render",d],["__file","post-67-2015-09-10.html.vue"]]);export{u as default};
