import{_ as e}from"./plugin-vue_export-helper-c27b6911.js";import{r as l,o,c as r,a as n,b as a,e as t,d as p,f as g}from"./app-39f3f472.js";const s={},h=a("p",null,[a("strong",null,"合歡尖山"),t("，海拔3217公尺，無基石，因山勢尖挺而聞名，該山雖未列入百岳之一，但山頂視野極佳，與其他合歡群峰一樣，可環視合歡山各個山頭及奇萊連峰等高山百岳。")],-1),c=a("p",null,"合歡尖山登山口，南北各一，南登山口位於合歡山莊旁，北登山口位於舊寒訓中心後方(石門山步道口對面)，上午走完合歡北峰後，因時間尚早，順道走走未曾造訪過的合歡尖山，原路來回(含休息)僅需25分鐘。",-1),d=g('<p>走走日期：2015/06/28</p><h2 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h2><p>從合歡山北(西)峰登山口往清境農場方向行駛，至台14甲33.5Km舊寒訓中心小停車場停車。</p><h2 id="走走路線" tabindex="-1"><a class="header-anchor" href="#走走路線" aria-hidden="true">#</a> 走走路線</h2><p>→舊寒訓中心停車，仰望合歡尖山<br><img src="https://1013399.github.io/image-2/69/1088914677_l.jpg" alt="IMGP8101.JPG - 合歡尖山" loading="lazy"></p><p>→合歡尖山登山口，開始陡上枕木步道<br><img src="https://1013399.github.io/image-2/69/1088915765_l.jpg" alt="IMGP8102.JPG - 合歡尖山" loading="lazy"></p><p>→接木棧道，依舊陡上<br><img src="https://1013399.github.io/image-2/69/1088914678_l.jpg" alt="IMGP8103.JPG - 合歡尖山" loading="lazy"></p><p>→步道轉為土階，就快到山頂了<br><img src="https://1013399.github.io/image-2/69/1088913686_l.jpg" alt="IMGP8104.JPG - 合歡尖山" loading="lazy"></p><p>→約10分鐘就可到達合歡尖山山頂<br><img src="https://1013399.github.io/image-2/69/1088914362_l.jpg" alt="IMGP8105.JPG - 合歡尖山" loading="lazy"></p><p>→合歡尖山海拔標示，山頂展望極佳<br><img src="https://1013399.github.io/image-2/69/1088916448_l.jpg" alt="IMGP8106.JPG - 合歡尖山" loading="lazy"></p><p>→眺望合歡主峰<br><img src="https://1013399.github.io/image-2/69/1088915174_l.jpg" alt="IMGP8109.JPG - 合歡尖山" loading="lazy"></p><p>→眺望合歡東峰<br><img src="https://1013399.github.io/image-2/69/1088917245_l.jpg" alt="IMGP8110.JPG - 合歡尖山" loading="lazy"></p><p>→眺望奇萊山<br><img src="https://1013399.github.io/image-2/69/1088914983_l.jpg" alt="IMGP8111.JPG - 合歡尖山" loading="lazy"></p><p>→眺望石門山與合歡北峰<br><img src="https://1013399.github.io/image-2/69/1088916356_l.jpg" alt="IMGP8112.JPG - 合歡尖山" loading="lazy"></p><p>→俯瞰合歡山莊、松雪樓與滑雪山莊<br><img src="https://1013399.github.io/image-2/69/1088916357_l.jpg" alt="IMGP8113.JPG - 合歡尖山" loading="lazy"></p><p>→原路折返</p><p>→回到登山口<br><img src="https://1013399.github.io/image-2/69/1088915656_l.jpg" alt="IMGP8114.JPG - 合歡尖山" loading="lazy"></p><h2 id="走走資訊" tabindex="-1"><a class="header-anchor" href="#走走資訊" aria-hidden="true">#</a> 走走資訊</h2><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>走走時間：約25分鐘(含休息、賞景)</p><p>高低落差：約60公尺</p></div><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2>',20),m={href:"https://f3bc5bac486d13952f7b473abc7ff0120f2fadb0.googledrive.com/host/0ByHgwY2tmBQJMnk1TnVUZnZSbG8/w426/index.htm",target:"_blank",rel:"noopener noreferrer"},b=a("figure",null,[a("img",{src:"https://1013399.github.io/image-2/69/1088916045_l.jpg",alt:"合歡尖山",tabindex:"0",loading:"lazy"}),a("figcaption",null,"合歡尖山")],-1);function _(u,G){const i=l("ExternalLinkIcon");return o(),r("div",null,[h,c,n(" more "),d,a("p",null,[t("原圖連結→"),a("a",m,[t("偶而獨步山林間(蕭郎)"),p(i)])]),b])}const y=e(s,[["render",_],["__file","post-69-2015-07-01.html.vue"]]);export{y as default};
