import{_ as l}from"./plugin-vue_export-helper-c27b6911.js";import{r as g,o as p,c as o,a as e,b as i,e as a,d as r,f as s}from"./app-272e1b70.js";const h={},n=i("p",null,[i("strong",null,"合歡山北峰"),a("，又稱北合歡山，海拔3422公尺，為合歡群峰中的最高峰，山頂有顆二等三角點No.1451，步道單程約2公里，沿途視野寬闊，山頂展望極佳，山勢為百岳十崇之一，名列台灣百岳No.34。")],-1),m=i("p",null,"合歡北峰與合歡西峰登山口相同，走訪合歡北峰也可順訪合歡西峰，惟路程較遠且耗時，當日往返恐摸黑下山，故不在今日行程內。",-1),c=s('<p>走走日期：2015/06/28</p><h2 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h2><p>國道3號轉國道6號，至國道6號終點埔里，接埔霧公路(台14線)左轉往霧社，過了霧社轉台14甲往清境農場，經過清境農場後續行至台14甲37.5Km合歡山北(西)峰登山口附近停車。</p><h2 id="走走路線" tabindex="-1"><a class="header-anchor" href="#走走路線" aria-hidden="true">#</a> 走走路線</h2><p>→合歡山北(西)峰登山口<br><img src="https://1013399.github.io/image-2/70/1088915167_l.jpg" alt="IMGP8050.JPG - 合歡山北峰" loading="lazy"></p><p>→一路陡上，高山不宜快走<br><img src="https://1013399.github.io/image-2/70/1088915852_l.jpg" alt="IMGP8052-54.JPG - 合歡山北峰" loading="lazy"></p><p>→沿途盛開的杜鵑已接近尾聲<br><img src="https://1013399.github.io/image-2/70/1088914861_l.jpg" alt="IMGP8055.JPG - 合歡山北峰" loading="lazy"></p><p>→0.1K里程樁，每100公尺設一根里程樁<br><img src="https://1013399.github.io/image-2/70/1088914171_l.jpg" alt="IMGP8056.JPG - 合歡山北峰" loading="lazy"></p><p>→經過峭壁<br><img src="https://1013399.github.io/image-2/70/1088914075_l.jpg" alt="IMGP8057.JPG - 合歡山北峰" loading="lazy"></p><p>→前方山頭約在1.1K~1.2K附近，今日登合歡北峰者眾<br><img src="https://1013399.github.io/image-2/70/1088912991_l.jpg" alt="IMGP8061.JPG - 合歡山北峰" loading="lazy"></p><p>→沿途展望佳，往花蓮方向眺望<br><img src="https://1013399.github.io/image-2/70/1088913584_l.jpg" alt="IMGP8062.JPG - 合歡山北峰" loading="lazy"></p><p>→眺望奇萊北峰與主峰<br><img src="https://1013399.github.io/image-2/70/1088913962_l.jpg" alt="IMGP8063.JPG - 合歡山北峰" loading="lazy"></p><p>→往回俯瞰小風口(合歡山管理站)<br><img src="https://1013399.github.io/image-2/70/1088913963_l.jpg" alt="IMGP8064.JPG - 合歡山北峰" loading="lazy"></p><p>→眺望中央尖山<br><img src="https://1013399.github.io/image-2/70/1088914458_l.jpg" alt="IMGP8067.JPG - 合歡山北峰" loading="lazy"></p><p>→眺望石門山、合歡東峰與主峰<br><img src="https://1013399.github.io/image-2/70/1088914970_l.jpg" alt="IMGP8068.JPG - 合歡山北峰" loading="lazy"></p><p>→1K里程樁，步道中點<br><img src="https://1013399.github.io/image-2/70/1088915854_l.jpg" alt="IMGP8069.JPG - 合歡山北峰" loading="lazy"></p><p>→1.2K里程樁，已可看見右側山頭的反射板<br><img src="https://1013399.github.io/image-2/70/1088914670_l.jpg" alt="IMGP8071.JPG - 合歡山北峰" loading="lazy"></p><p>→過了1.2K後，步道趨緩<br><img src="https://1013399.github.io/image-2/70/1088915455_l.jpg" alt="IMGP8072.JPG - 合歡山北峰" loading="lazy"></p><p>→1.3K叉路，先取右往反射板一探<br><img src="https://1013399.github.io/image-2/70/1088915254_l.jpg" alt="IMGP8073.JPG - 合歡山北峰" loading="lazy"></p><p>→左前方山頭便是合歡山北峰<br><img src="https://1013399.github.io/image-2/70/1088914459_l.jpg" alt="IMGP8074.JPG - 合歡山北峰" loading="lazy"></p><p>→反射板，主要是用來加強接收遠方之超短波、微波等信號<br><img src="https://1013399.github.io/image-2/70/1088915169_l.jpg" alt="IMGP8075.JPG - 合歡山北峰" loading="lazy"></p><p>→續往前行，約在1.6K接回步道<br><img src="https://1013399.github.io/image-2/70/1088915562_l.jpg" alt="IMGP8077.JPG - 合歡山北峰" loading="lazy"></p><p>→天巒池叉路，約在1.8K處，右可往北鞍營地、天巒池，取左續行<br><img src="https://1013399.github.io/image-2/70/1088914671_l.jpg" alt="IMGP8078.JPG - 合歡山北峰" loading="lazy"></p><p>→最後攻頂<br><img src="https://1013399.github.io/image-2/70/1088915255_l.jpg" alt="IMGP8079.JPG - 合歡山北峰" loading="lazy"></p><p>→合歡山北峰山頂，山頂廣闊<br><img src="https://1013399.github.io/image-2/70/1088915652_l.jpg" alt="IMGP8080.JPG - 合歡山北峰" loading="lazy"></p><p>→合歡北峰，二等三角點No.1451<br><img src="https://1013399.github.io/image-2/70/1088915563_l.jpg" alt="IMGP8081-82.JPG - 合歡山北峰" loading="lazy"></p><p>→合歡北峰，山頂標示牌<br><img src="https://1013399.github.io/image-2/70/1088913586_l.jpg" alt="IMGP8084.JPG - 合歡山北峰" loading="lazy"></p><p>→俯瞰華崗地區<br><img src="https://1013399.github.io/image-2/70/1088913587_l.jpg" alt="IMGP8085.JPG - 合歡山北峰" loading="lazy"></p><p>→續行可往合歡山西峰<br><img src="https://1013399.github.io/image-2/70/1088913588_l.jpg" alt="IMGP8086.JPG - 合歡山北峰" loading="lazy"></p><p>→與同行山友拍張合照，在此休息、午餐<br><img src="https://1013399.github.io/image-2/70/1088913278_l.jpg" alt="IMGP8088.JPG - 合歡山北峰" loading="lazy"></p><p>→餐畢，原路折返</p><p>→回到1.6K叉路，左往反射板，取右回<br><img src="https://1013399.github.io/image-2/70/1088915653_l.jpg" alt="IMGP8092.JPG - 合歡山北峰" loading="lazy"></p><p>→開始陡下<br><img src="https://1013399.github.io/image-2/70/1088913676_l.jpg" alt="IMGP8094.JPG - 合歡山北峰" loading="lazy"></p><p>→回到0.5K附近，人多路窄易塞車<br><img src="https://1013399.github.io/image-2/70/1088914862_l.jpg" alt="IMGP8095.JPG - 合歡山北峰" loading="lazy"></p><p>→回到登山口<br><img src="https://1013399.github.io/image-2/70/1088914674_l.jpg" alt="IMGP8096.JPG - 合歡山北峰" loading="lazy"></p><p>→回到停車處<br><img src="https://1013399.github.io/image-2/70/1088913679_l.jpg" alt="IMGP8097.JPG - 合歡山北峰" loading="lazy"></p><p>→停車處附近有個測量控制點<br><img src="https://1013399.github.io/image-2/70/1088913874_l.jpg" alt="IMGP8098.JPG - 合歡山北峰" loading="lazy"></p><h2 id="走走資訊" tabindex="-1"><a class="header-anchor" href="#走走資訊" aria-hidden="true">#</a> 走走資訊</h2><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>走走時間：約4小時(含休息、午餐、賞景)</p><p>高低落差：約450公尺</p></div><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2><p>(裁剪過原圖)</p>',41),b={href:"http://www.keepon.com.tw/thread-49405a11-12d8-e411-93ec-000e04b74954.html",target:"_blank",rel:"noopener noreferrer"},d=i("br",null,null,-1),G=i("img",{src:"https://1013399.github.io/image-2/70/1088914077_l.jpg",alt:"合歡山北峰",loading:"lazy"},null,-1);function P(_,u){const t=g("ExternalLinkIcon");return p(),o("div",null,[n,m,e(" more "),c,i("p",null,[a("原圖連結→"),i("a",b,[a("登山補給站(brownlee小李)"),r(t)]),d,G])])}const y=l(h,[["render",P],["__file","post-70-2015-06-30.html.vue"]]);export{y as default};
