import{_ as r}from"./plugin-vue_export-helper-c27b6911.js";import{r as i,o as p,c as g,a as n,b as t,d as e,w as s,e as a,f as h}from"./app-e90ae243.js";const c={},d=t("p",null,[t("strong",null,"合歡山東峰"),a("，海拔3421公尺，是合歡群峰的第二高峰，也是賞高山杜鵑的最佳勝地，步道單程1.3公里，較主峰陡峭，山勢巍峨，為百岳九峨之一，山頂展望極佳。合歡山主峰名列為台灣百岳No.35，也是百岳之中難度不高之一。")],-1),m=t("p",null,"昨日夜宿滑雪山莊，根據中央氣象局APP顯示，今日06:00前降雨機率為20%，06:00~12:00為50%，12:00以後則為100%，清晨約05:00起床，發現雲霧稍散，可見近山雲海，既然已取消合歡山北峰及西峰之行，決定趁未下雨之前走走昨天沒完成的合歡山東峰。",-1),_=h('<p>走走日期：2015/04/11</p><h2 id="走走路線" tabindex="-1"><a class="header-anchor" href="#走走路線" aria-hidden="true">#</a> 走走路線</h2><p>→05:30滑雪山莊出發<br><img src="https://1013399.github.io/image-2/74/1079119676_l.jpg" alt="合歡山東峰" loading="lazy"></p><p>→往松雪樓方向走<br><img src="https://1013399.github.io/image-2/74/1079119780_l.jpg" alt="合歡山東峰" loading="lazy"></p><p>→步道叉路，右往松雪樓，取左往合歡山東峰<br><img src="https://1013399.github.io/image-2/74/1079120443_l.jpg" alt="合歡山東峰" loading="lazy"></p><p>→松雪樓位於步道右側<br><img src="https://1013399.github.io/image-2/74/1079120244_l.jpg" alt="合歡山東峰" loading="lazy"></p><p>→接上松雪樓過來的步道，往右可往松雪樓，取左上<br><img src="https://1013399.github.io/image-2/74/1079119513_l.jpg" alt="合歡山東峰" loading="lazy"></p><p>→合歡東峰步道是由枕木階梯與木棧階梯組成，一路陡上<br><img src="https://1013399.github.io/image-2/74/1079120065_l.jpg" alt="合歡山東峰" loading="lazy"></p><p>→步道500m，不知是走了500m還是距離山頂500m<br><img src="https://1013399.github.io/image-2/74/1079120345_l.jpg" alt="合歡山東峰" loading="lazy"></p><p>→休息小平台，後方為昔日滑雪用的廢棄纜車站<br><img src="https://1013399.github.io/image-2/74/1079119971_l.jpg" alt="合歡山東峰" loading="lazy"></p><p>→纜車站解說牌，休息後繼續陡上、很喘<br><img src="https://1013399.github.io/image-2/74/1079119306_l.jpg" alt="合歡山東峰" loading="lazy"></p><p>→步道轉為山徑<br><img src="https://1013399.github.io/image-2/74/1079119871_l.jpg" alt="合歡山東峰" loading="lazy"></p><p>→叉路，右下山徑可往合歡山莊停車場，開始出現杜鵑(尚未開花)<br><img src="https://1013399.github.io/image-2/74/1079120447_l.jpg" alt="合歡山東峰" loading="lazy"></p><p>→東峰前小山頭，設有眺望各方向山頭之解說牌<br><img src="https://1013399.github.io/image-2/74/1079120449_l.jpg" alt="合歡山東峰" loading="lazy"></p><p>→合歡山東峰，山頂設有標示木柱，休息後原路折返<br><img src="https://1013399.github.io/image-2/74/1079120068_l.jpg" alt="合歡山東峰" loading="lazy"></p><p>→回到松雪樓叉路，前往松雪樓享用早餐<br><img src="https://1013399.github.io/image-2/74/1079120070_l.jpg" alt="合歡山東峰" loading="lazy"></p><p>→松雪樓目前溫度5.2℃，吃完早餐又開始下起雨了<br><img src="https://1013399.github.io/image-2/74/1079120743_l.jpg" alt="合歡山東峰" loading="lazy"></p><h2 id="走走資訊" tabindex="-1"><a class="header-anchor" href="#走走資訊" aria-hidden="true">#</a> 走走資訊</h2><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>走走時間：約1小時50分鐘(含休息)</p><p>高低落差：約280公尺</p></div><h2 id="上一篇相關文章" tabindex="-1"><a class="header-anchor" href="#上一篇相關文章" aria-hidden="true">#</a> 上一篇相關文章</h2>',20),b=t("h2",{id:"參考地圖",tabindex:"-1"},[t("a",{class:"header-anchor",href:"#參考地圖","aria-hidden":"true"},"#"),a(" 參考地圖")],-1),u={href:"http://recreation.forest.gov.tw/RA/RA_1_1.aspx?RA_ID=0300004",target:"_blank",rel:"noopener noreferrer"},f=t("figure",null,[t("img",{src:"https://1013399.github.io/image-2/74/1079120749_l.jpg",alt:"合歡山東峰",tabindex:"0",loading:"lazy"}),t("figcaption",null,"合歡山東峰")],-1);function j(y,z){const l=i("RouterLink"),o=i("ExternalLinkIcon");return p(),g("div",null,[d,m,n(" more "),_,t("p",null,[e(l,{to:"/posts/post-75-2015-04-15.html"},{default:s(()=>[a("合歡山主峰")]),_:1})]),b,t("p",null,[a("原圖連結→"),t("a",u,[a("台灣山林悠遊網"),e(o)])]),f])}const v=r(c,[["render",j],["__file","post-74-2015-04-16.html.vue"]]);export{v as default};
