import{_ as g}from"./plugin-vue_export-helper-c27b6911.js";import{r as e,o as r,c as s,b as t,f as i,d as a,w as n,e as o}from"./app-867f9a58.js";const h={},d=o('<p><strong>合歡山主峰</strong>，海拔3416公尺，步道單程1.8公里，沿途視野寬闊，山頂展望極佳，合歡群峰、奇萊連峰等高山百岳一覽無遺。合歡山主峰山勢圓潤，為百岳十潤之一，名列為台灣百岳No.36，也算是百岳入門等級之一。<br>     應同事之邀，計畫明日(4/11)走走合歡山北峰及西峰賞杜鵑，由於西峰路程較遠較耗時，預計清晨5點出發(寧願早摸黑上山、也不要晚摸黑下山)，因此決定提前一天上合歡山(夜宿滑雪山莊)，先適應高山海拔，順便走走未曾走過的合歡山主峰及東峰，來到合歡山，一片濃霧，一副山雨欲來之勢，果然走完合歡山主峰，便下起大雨，連合歡山東峰都沒法走了，而且杜鵑花也僅僅含苞待放而已。</p><p>走走日期：2015/04/10</p><h2 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h2><p>國道3號轉國道6號，至國道6號終點埔里，接埔霧公路(台14線)左轉往霧社，過了霧社轉台14甲往清境農場，經過清境農場後續行至台14甲30.8Km合歡山主峰登山口停車。</p><h2 id="走走路線" tabindex="-1"><a class="header-anchor" href="#走走路線" aria-hidden="true">#</a> 走走路線</h2><p>→合歡山登山口，設有路障，走車道往上行<br><img src="https://1013399.github.io/image-2/75/1079114489_l.jpg" alt="合歡山主峰" loading="lazy"></p><p>→車道閘門，非公務車輛不可進入<br><img src="https://1013399.github.io/image-2/75/1079113201_l.jpg" alt="合歡山主峰" loading="lazy"></p><p>→一片濃霧，「沒」景當前<br><img src="https://1013399.github.io/image-2/75/1079113202_l.jpg" alt="合歡山主峰" loading="lazy"></p><p>→第一觀景台<br><img src="https://1013399.github.io/image-2/75/1079115080_l.jpg" alt="合歡山主峰" loading="lazy"></p><p>→叉路，右側可下切至台14甲31.1Km第二登山口<br><img src="https://1013399.github.io/image-2/75/1079113203_l.jpg" alt="合歡山主峰" loading="lazy"></p><p>→第二觀景台<br><img src="https://1013399.github.io/image-2/75/1079115276_l.jpg" alt="合歡山主峰" loading="lazy"></p><p>→觀景台旁不知名植物，不知是花還是葉<br><img src="https://1013399.github.io/image-2/75/1079114883_l.jpg" alt="合歡山主峰" loading="lazy"></p><p>→玉山圓柏<br><img src="https://1013399.github.io/image-2/75/1079113409_l.jpg" alt="合歡山主峰" loading="lazy"></p><p>→叉路，右側可下切至武嶺停車場<br><img src="https://1013399.github.io/image-2/75/1079113204_l.jpg" alt="合歡山主峰" loading="lazy"></p><p>→碉堡<br><img src="https://1013399.github.io/image-2/75/1079115596_l.jpg" alt="合歡山主峰" loading="lazy"></p><p>→公共廁所<br><img src="https://1013399.github.io/image-2/75/1079113690_l.jpg" alt="合歡山主峰" loading="lazy"></p><p>→過公廁後右側有枕木階梯叉路，走車道或階梯皆可<br><img src="https://1013399.github.io/image-2/75/1079115186_l.jpg" alt="合歡山主峰" loading="lazy"></p><p>→取枕木階梯上行<br><img src="https://1013399.github.io/image-2/75/1079113691_l.jpg" alt="合歡山主峰" loading="lazy"></p><p>→接回車道，也是車道終點，取右上<br><img src="https://1013399.github.io/image-2/75/1079113207_l.jpg" alt="合歡山主峰" loading="lazy"></p><p>→微波站及基地台<br><img src="https://1013399.github.io/image-2/75/1079113623_l.jpg" alt="合歡山主峰" loading="lazy"></p><p>→合歡山主峰，山頂標示石碑、不鏽鋼柱及三等三角點No.6388<br><img src="https://1013399.github.io/image-2/75/1079115945_l.jpg" alt="合歡山主峰" loading="lazy"></p><p>→含苞待放的高山杜鵑，原路折返，改走車道下山<br><img src="https://1013399.github.io/image-2/75/1079113624_l.jpg" alt="合歡山主峰" loading="lazy"></p><p>→回到登山口<br><img src="https://1013399.github.io/image-2/75/1079114389_l.jpg" alt="合歡山主峰" loading="lazy"></p><h2 id="走走資訊" tabindex="-1"><a class="header-anchor" href="#走走資訊" aria-hidden="true">#</a> 走走資訊</h2><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>走走時間：約1.5小時(含休息)</p><p>高低落差：約200公尺</p></div><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2>',26),c={href:"http://recreation.forest.gov.tw/RA/RA_1_1.aspx?RA_ID=0300004",target:"_blank",rel:"noopener noreferrer"},_=t("br",null,null,-1),m=t("img",{src:"https://1013399.github.io/image-2/75/1079118969_l.jpg",alt:"合歡山主峰",loading:"lazy"},null,-1),b=o('<p>→取車至合歡山莊停車場午餐，開始下起大雨</p><p>→雨勢未歇，在車上午休，待到15:00至松雪樓CHECK IN<br><img src="https://1013399.github.io/image-2/75/1079115600_l.jpg" alt="合歡山主峰" loading="lazy"></p><p>→走松雪樓旁步道往滑雪山莊，這也是合歡山東峰的登山口之一<br><img src="https://1013399.github.io/image-2/75/1079114390_l.jpg" alt="合歡山主峰" loading="lazy"></p><p>→滑雪山莊<br><img src="https://1013399.github.io/image-2/75/1079114493_l.jpg" alt="合歡山主峰" loading="lazy"></p><p>→滑雪山莊旁邊即為奇萊山登山口<br><img src="https://1013399.github.io/image-2/75/1079113626_l.jpg" alt="合歡山主峰" loading="lazy"></p><p>→登山口旁導覽圖<br><img src="https://1013399.github.io/image-2/75/1079113693_l.jpg" alt="合歡山主峰" loading="lazy"></p><p>→不怕人的酒紅朱雀和金翼畫眉<br><img src="https://1013399.github.io/image-2/75/1079214241_l.jpg" alt="合歡山主峰" loading="lazy"></p><h2 id="下一篇相關文章" tabindex="-1"><a class="header-anchor" href="#下一篇相關文章" aria-hidden="true">#</a> 下一篇相關文章</h2>',8),u=t("h2",{id:"※有關滑雪山莊住宿補充說明",tabindex:"-1"},[t("a",{class:"header-anchor",href:"#※有關滑雪山莊住宿補充說明","aria-hidden":"true"},"#"),i(" ※有關滑雪山莊住宿補充說明：")],-1),j={href:"http://tsfs.forest.gov.tw/cht/index.php",target:"_blank",rel:"noopener noreferrer"},y=t("li",null,"都是通鋪，每人每晚1000元，自備盥洗用具則減100元(即900元)。",-1),z=t("li",null,"含當天晚餐及隔天早餐。",-1),f=t("li",null,"報到及用餐在松雪樓。",-1),x=t("li",null,"隔天早餐無法享用，可先至松雪樓餐廳換取等值乾糧。",-1),k=t("li",null,"報到時，可押證件免費租用電毯。",-1),v=t("li",null,"滑雪山莊二樓設有純氧製造機，方便不適應高山氣候之遊客使用。",-1),N=t("li",null,"其他注意事項，請參考該網站。",-1);function R(w,C){const l=e("ExternalLinkIcon"),p=e("RouterLink");return r(),s("div",null,[d,t("p",null,[i("原圖連結→"),t("a",c,[i("台灣山林悠遊網"),a(l)]),_,m]),b,t("p",null,[a(p,{to:"/posts/post-74-2015-04-16.html"},{default:n(()=>[i("合歡山東峰)")]),_:1})]),u,t("ol",null,[t("li",null,[i("在「"),t("strong",null,[t("a",j,[i("東勢林區管理處遊客訂房服務中心"),a(l)])]),i("」網站訂房。")]),y,z,f,x,k,v,N])])}const L=g(h,[["render",R],["__file","post-75-2015-04-15.html.vue"]]);export{L as default};
