import{_ as a}from"./plugin-vue_export-helper-c27b6911.js";import{o as t,c as g,a as l,b as i,e as p,f as r}from"./app-39f3f472.js";const o={},s=i("p",null,[i("strong",null,"新林步道"),p("，位於新北市林口台地東南邊與泰山區交界之山麓沿線，係由林口登山委員會之山友義工闢建而成，沿途多處舖設地毯頗具特色，途中可欣賞高速公路車流，也可串連老公崎步道賞油桐花。")],-1),h=i("p",null,"新林步道，入口在師範大學林口分部，出口在林口高中，步道分為五段，第一段長480公尺，第二段長320公尺，為陡下之好漢坡，第三段長950公尺，第四段長300公尺，第五段長1000公尺，全程長3公里多，上下起伏落差約200公尺，需注意，沿途有許多流浪犬，有些會狂吠，不予理會，慢慢通過即可。",-1),e=r('<p>走走日期：2015/02/15</p><h2 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h2><p>國道1號北上出林口交流道，左轉文化一路再右轉仁愛路，至仁愛路一段2號的師範大學林口分部，停車於新林福德宮前。</p><h2 id="走走路線" tabindex="-1"><a class="header-anchor" href="#走走路線" aria-hidden="true">#</a> 走走路線</h2><p>→新林福德宮前停車，有指標可往老公崎步道，進入新寮路往新林步道<br><img src="https://1013399.github.io/image-2/79/1071706615_l.jpg" alt="新林步道" loading="lazy"></p><p>→路口設有步道導覽圖<br><img src="https://1013399.github.io/image-2/79/1071706429_l.jpg" alt="新林步道" loading="lazy"></p><p>→約行300公尺叉路取右行<br><img src="https://1013399.github.io/image-2/79/1071703977_l.jpg" alt="新林步道" loading="lazy"></p><p>→叉路口電桿「新寮幹33」及指標<br><img src="https://1013399.github.io/image-2/79/1071703579_l.jpg" alt="新林步道" loading="lazy"></p><p>→新林步道第一段入口，設有牌樓、導覽圖，步道緩下<br><img src="https://1013399.github.io/image-2/79/1071705189_l.jpg" alt="新林步道" loading="lazy"></p><p>→叉路，右為老公崎步道，可往笨箕湖，取左下<br><img src="https://1013399.github.io/image-2/79/1071707319_l.jpg" alt="新林步道" loading="lazy"></p><p>→好走的地毯步道<br><img src="https://1013399.github.io/image-2/79/1071705535_l.jpg" alt="新林步道" loading="lazy"></p><p>→小木橋，過橋後步道轉為向上<br><img src="https://1013399.github.io/image-2/79/1071703580_l.jpg" alt="新林步道" loading="lazy"></p><p>→觀景點(賞車亭)<br><img src="https://1013399.github.io/image-2/79/1071706358_l.jpg" alt="新林步道" loading="lazy"></p><p>→俯瞰蜿蜒的中山高<br><img src="https://1013399.github.io/image-2/79/1071705191_l.jpg" alt="新林步道" loading="lazy"></p><p>→取右續行<br><img src="https://1013399.github.io/image-2/79/1071707320_l.jpg" alt="新林步道" loading="lazy"></p><p>→高點叉路，右側有休息桌椅、涼棚，可往新林福德宮，休息<br><img src="https://1013399.github.io/image-2/79/1071707916_l.jpg" alt="新林步道" loading="lazy"></p><p>→第一段步道至此結束，此處也可俯瞰中山高<br><img src="https://1013399.github.io/image-2/79/1071703979_l.jpg" alt="新林步道" loading="lazy"></p><p>→續行進入第二段步道，一路急陡下<br><img src="https://1013399.github.io/image-2/79/1071707730_l.jpg" alt="新林步道" loading="lazy"></p><p>→此段稱為好漢坡<br><img src="https://1013399.github.io/image-2/79/1071704289_l.jpg" alt="新林步道" loading="lazy"></p><p>→義工大橋，第二段步道結束<br><img src="https://1013399.github.io/image-2/79/1071704774_l.jpg" alt="新林步道" loading="lazy"></p><p>→過橋後，左可往大科路，第三段步道入口在正前方<br><img src="https://1013399.github.io/image-2/79/1071707732_l.jpg" alt="新林步道" loading="lazy"></p><p>→穿越馬路進入第三段步道<br><img src="https://1013399.github.io/image-2/79/1071707733_l.jpg" alt="新林步道" loading="lazy"></p><p>→第三段步道一開始為緩上寬水泥路<br><img src="https://1013399.github.io/image-2/79/1071704775_l.jpg" alt="新林步道" loading="lazy"></p><p>→寬水泥路結束，轉為陡上<br><img src="https://1013399.github.io/image-2/79/1071706068_l.jpg" alt="新林步道" loading="lazy"></p><p>→沿途有許多廢棄石桌椅，此處為泰山森林遊樂區的舊址<br><img src="https://1013399.github.io/image-2/79/1071707734_l.jpg" alt="新林步道" loading="lazy"></p><p>→繼續陡上，巧遇萬華山岳會山友<br><img src="https://1013399.github.io/image-2/79/1071707735_l.jpg" alt="新林步道" loading="lazy"></p><p>→上至小山頭，有棵樟樹<br><img src="https://1013399.github.io/image-2/79/1071707917_l.jpg" alt="新林步道" loading="lazy"></p><p>→步道轉為平緩山腰路<br><img src="https://1013399.github.io/image-2/79/1071705192_l.jpg" alt="新林步道" loading="lazy"></p><p>→接上寬大土路<br><img src="https://1013399.github.io/image-2/79/1071704183_l.jpg" alt="新林步道" loading="lazy"></p><p>→叉路，直行可往林口區公所，取左進入第四段步道<br><img src="https://1013399.github.io/image-2/79/1071704292_l.jpg" alt="新林步道" loading="lazy"></p><p>→第四段步道和第二段一樣，一路陡下<br><img src="https://1013399.github.io/image-2/79/1071705193_l.jpg" alt="新林步道" loading="lazy"></p><p>→接馬路，取左行<br><img src="https://1013399.github.io/image-2/79/1071706432_l.jpg" alt="新林步道" loading="lazy"></p><p>→叉路，右前方為「包清府」，左可往大科路，取右往第五段步道<br><img src="https://1013399.github.io/image-2/79/1071703582_l.jpg" alt="新林步道" loading="lazy"></p><p>→沿溝堤進入第五段步道<br><img src="https://1013399.github.io/image-2/79/1071707138_l.jpg" alt="新林步道" loading="lazy"></p><p>→第五段步道入口<br><img src="https://1013399.github.io/image-2/79/1071703583_l.jpg" alt="新林步道" loading="lazy"></p><p>→步道緩上<br><img src="https://1013399.github.io/image-2/79/1071706069_l.jpg" alt="新林步道" loading="lazy"></p><p>→接柏油路，左可往麗園一街，右前方為青春嶺森林遊樂區舊址，取右上往林口高中<br><img src="https://1013399.github.io/image-2/79/1071707919_l.jpg" alt="新林步道" loading="lazy"></p><p>→林口高中，第五段步道出口，接四維路，開始踢馬路<br><img src="https://1013399.github.io/image-2/79/1071705196_l.jpg" alt="新林步道" loading="lazy"></p><p>→四維路與仁愛路交叉口，取右回往師範大學<br><img src="https://1013399.github.io/image-2/79/1071707625_l.jpg" alt="新林步道" loading="lazy"></p><p>→仁愛路上有多處老公崎步道的入口<br><img src="https://1013399.github.io/image-2/79/1071706073_l.jpg" alt="新林步道" loading="lazy"></p><p>→回到師範大學林口分部<br><img src="https://1013399.github.io/image-2/79/1071707323_l.jpg" alt="新林步道" loading="lazy"></p><p>→回到停車處</p><h2 id="走走資訊" tabindex="-1"><a class="header-anchor" href="#走走資訊" aria-hidden="true">#</a> 走走資訊</h2><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>走走時間：約2小時(含踢馬路40分鐘)</p><p>高低落差：</p><ul><li>第一段  長480公尺    約下降50公尺再上升30公尺</li><li>第二段  長320公尺    約下降80公尺</li><li>第三段  長950公尺    約上升100公尺</li><li>第四段  長300公尺    約下降75公尺</li><li>第五段  長1000公尺   約上升75公尺</li></ul></div><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2><p>(拍攝自導覽圖)</p><figure><img src="https://1013399.github.io/image-2/79/1071706620_l.jpg" alt="新林步道" tabindex="0" loading="lazy"><figcaption>新林步道</figcaption></figure>',47);function m(b,n){return t(),g("div",null,[s,h,l(" more "),e])}const _=a(o,[["render",m],["__file","post-79-2015-02-17.html.vue"]]);export{_ as default};
