import{_ as g}from"./plugin-vue_export-helper-c27b6911.js";import{r as l,o as p,c as o,a as r,b as i,e as t,d as s,f as e}from"./app-455fc4b3.js";const h={},n=i("p",null,[i("strong",null,"鵝公髻山"),t("，海拔1579公尺，位於新竹縣五峰鄉與苗栗縣南庄鄉交界，北接北埔五指山，南接五峰鳥嘴山、大窩山，由遠處觀之，山形像公鵝的髻而得名，山頂有顆三等三角點No.6239，名列台灣小百岳No.031。")],-1),m=i("p",null,[i("strong",null,"鵝公髻山東北峰"),t("，海拔1182公尺，位於新竹縣五峰鄉，有顆森林三角點，基石位於崖邊，岌岌可危，有山友特別用繩子綁住，樹林間無展望。")],-1),b=e('<p>鵝公髻山有數個登山口，第一及第二登山口位於五峰鄉大隘派出所(已廢棄)約1公里及2.2公里處，另外還有「山上人家」登山口，屬較輕鬆路線，而南庄鄉東河村金鵝橋登山口則落差較大、難度較高。</p><p>從山上人家登鵝公髻山，已走過三次，這次與同事相約從第一登山口起登，第二登山口下，再走回第一登山口(一上二下)，幸運的是，下到第二登山口水泥農路時，搭乘湖口山友張先生便車回到第一登山口，少走約1.6公里的農路及馬路，真是感恩。</p><p>走走日期：2015/01/25</p><h2 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h2><p>竹東行駛122縣道往五峰，於122縣道35.5K處右轉上大隘產業道路，依山上人家指標前進，經過大隘派出所不久，遇T叉路左轉(右往南庄)，至第一登山口停車場停車(122縣道行駛至此約10km)。</p><h2 id="走走路線" tabindex="-1"><a class="header-anchor" href="#走走路線" aria-hidden="true">#</a> 走走路線</h2><p>→鵝公髻山第一登山口停車場，掛有牌子寫著停車收費100元，但無人收費<br><img src="https://1013399.github.io/image-2/81/1068694593_l.jpg" alt="鵝公髻山東北峰、鵝公髻山" loading="lazy"></p><p>→出發，進入水泥農路<br><img src="https://1013399.github.io/image-2/81/1068693619_l.jpg" alt="鵝公髻山東北峰、鵝公髻山" loading="lazy"></p><p>→叉路，往左為續行水泥農路，右側有木棧階梯，左右皆可<br><img src="https://1013399.github.io/image-2/81/1068690929_l.jpg" alt="鵝公髻山東北峰、鵝公髻山" loading="lazy"></p><p>→取右上木棧階梯<br><img src="https://1013399.github.io/image-2/81/1068692011_l.jpg" alt="鵝公髻山東北峰、鵝公髻山" loading="lazy"></p><p>→休息木椅<br><img src="https://1013399.github.io/image-2/81/1068690527_l.jpg" alt="鵝公髻山東北峰、鵝公髻山" loading="lazy"></p><p>→小觀景台，視野不佳<br><img src="https://1013399.github.io/image-2/81/1068692012_l.jpg" alt="鵝公髻山東北峰、鵝公髻山" loading="lazy"></p><p>→接回水泥農路<br><img src="https://1013399.github.io/image-2/81/1068693232_l.jpg" alt="鵝公髻山東北峰、鵝公髻山" loading="lazy"></p><p>→右側有觀景台，先取右賞景<br><img src="https://1013399.github.io/image-2/81/1068693233_l.jpg" alt="鵝公髻山東北峰、鵝公髻山" loading="lazy"></p><p>→眺望五指山<br><img src="https://1013399.github.io/image-2/81/1068692815_l.jpg" alt="鵝公髻山東北峰、鵝公髻山" loading="lazy"></p><p>→退回，續行農路往上行，沿途開滿許多不知名白花<br><img src="https://1013399.github.io/image-2/81/1068692120_l.jpg" alt="鵝公髻山東北峰、鵝公髻山" loading="lazy"></p><p>→水泥路結束，接泥土路<br><img src="https://1013399.github.io/image-2/81/1068692817_l.jpg" alt="鵝公髻山東北峰、鵝公髻山" loading="lazy"></p><p>→沿途可眺望五指山<br><img src="https://1013399.github.io/image-2/81/1068690371_l.jpg" alt="鵝公髻山東北峰、鵝公髻山" loading="lazy"></p><p>→叉路，直行往鵝公髻山，先取右上往鵝公髻山東北峰<br><img src="https://1013399.github.io/image-2/81/1068693236_l.jpg" alt="鵝公髻山東北峰、鵝公髻山" loading="lazy"></p><p>→上小山頭後，取右彎<br><img src="https://1013399.github.io/image-2/81/1068691532_l.jpg" alt="鵝公髻山東北峰、鵝公髻山" loading="lazy"></p><p>→鵝公髻山東北峰，森林三角點基石位於崖邊，岌岌可危<br><img src="https://1013399.github.io/image-2/81/1068692601_l.jpg" alt="鵝公髻山東北峰、鵝公髻山" loading="lazy"></p><p>→鵝公髻山東北峰，繩子綁住基石，以免墜落，拍照後原路退回<br><img src="https://1013399.github.io/image-2/81/1068691157_l.jpg" alt="鵝公髻山東北峰、鵝公髻山" loading="lazy"></p><p>→回到前一叉路，取右續行<br><img src="https://1013399.github.io/image-2/81/1068693049_l.jpg" alt="鵝公髻山東北峰、鵝公髻山" loading="lazy"></p><p>→叉路，左下往第二登山口，續直行<br><img src="https://1013399.github.io/image-2/81/1068692017_l.jpg" alt="鵝公髻山東北峰、鵝公髻山" loading="lazy"></p><p>→陡上，旁有護欄<br><img src="https://1013399.github.io/image-2/81/1068692821_l.jpg" alt="鵝公髻山東北峰、鵝公髻山" loading="lazy"></p><p>→不知名紅色植物，不知是花還是葉<br><img src="https://1013399.github.io/image-2/81/1068693050_l.jpg" alt="鵝公髻山東北峰、鵝公髻山" loading="lazy"></p><p>→空曠休息處，原設桌椅已損壞，在此休息<br><img src="https://1013399.github.io/image-2/81/1068693834_l.jpg" alt="鵝公髻山東北峰、鵝公髻山" loading="lazy"></p><p>→續行，經過巨木<br><img src="https://1013399.github.io/image-2/81/1068692603_l.jpg" alt="鵝公髻山東北峰、鵝公髻山" loading="lazy"></p><p>→經過倒木後陡上<br><img src="https://1013399.github.io/image-2/81/1068691535_l.jpg" alt="鵝公髻山東北峰、鵝公髻山" loading="lazy"></p><p>→越過路中大石<br><img src="https://1013399.github.io/image-2/81/1068691162_l.jpg" alt="鵝公髻山東北峰、鵝公髻山" loading="lazy"></p><p>→再陡上<br><img src="https://1013399.github.io/image-2/81/1068691537_l.jpg" alt="鵝公髻山東北峰、鵝公髻山" loading="lazy"></p><p>→第二處休息空地，不休息續行<br><img src="https://1013399.github.io/image-2/81/1068693836_l.jpg" alt="鵝公髻山東北峰、鵝公髻山" loading="lazy"></p><p>→繼續陡上<br><img src="https://1013399.github.io/image-2/81/1068692604_l.jpg" alt="鵝公髻山東北峰、鵝公髻山" loading="lazy"></p><p>→叉路，右側為雜草捷徑，取左較好走<br><img src="https://1013399.github.io/image-2/81/1068694510_l.jpg" alt="鵝公髻山東北峰、鵝公髻山" loading="lazy"></p><p>→陡上，比之前更陡<br><img src="https://1013399.github.io/image-2/81/1068692124_l.jpg" alt="鵝公髻山東北峰、鵝公髻山" loading="lazy"></p><p>→路徑趨緩，開始出現箭竹<br><img src="https://1013399.github.io/image-2/81/1068690536_l.jpg" alt="鵝公髻山東北峰、鵝公髻山" loading="lazy"></p><p>→最後攻頂<br><img src="https://1013399.github.io/image-2/81/1068693244_l.jpg" alt="鵝公髻山東北峰、鵝公髻山" loading="lazy"></p><p>→鵝公髻山山頂<br><img src="https://1013399.github.io/image-2/81/1068692607_l.jpg" alt="鵝公髻山東北峰、鵝公髻山" loading="lazy"></p><p>→鵝公髻山，三等三角點No.6239<br><img src="https://1013399.github.io/image-2/81/1068693051_l.jpg" alt="鵝公髻山東北峰、鵝公髻山" loading="lazy"></p><p>→續行左可往山上人家、右可往南庄東河登山口<br><img src="https://1013399.github.io/image-2/81/1068690939_l.jpg" alt="鵝公髻山東北峰、鵝公髻山" loading="lazy"></p><p>→取右側雜草路徑往觀景點<br><img src="https://1013399.github.io/image-2/81/1068692609_l.jpg" alt="鵝公髻山東北峰、鵝公髻山" loading="lazy"></p><p>→眺望加里山<br><img src="https://1013399.github.io/image-2/81/1068693840_l.jpg" alt="鵝公髻山東北峰、鵝公髻山" loading="lazy"></p><p>→退回三角點附近，休息午餐後，原路折返</p><p>→途中發現已開花的根節蘭<br><img src="https://1013399.github.io/image-2/81/1068694610_l.jpg" alt="鵝公髻山東北峰、鵝公髻山" loading="lazy"></p><p>→回到第一與第二登山口的會合處，取右下山<br><img src="https://1013399.github.io/image-2/81/1068692436_l.jpg" alt="鵝公髻山東北峰、鵝公髻山" loading="lazy"></p><p>→一路陡下<br><img src="https://1013399.github.io/image-2/81/1068694511_l.jpg" alt="鵝公髻山東北峰、鵝公髻山" loading="lazy"></p><p>→途中有些地方設有護欄<br><img src="https://1013399.github.io/image-2/81/1068692823_l.jpg" alt="鵝公髻山東北峰、鵝公髻山" loading="lazy"></p><p>→往第二登山口大都穿梭在杉木林中，似乎也比較陡<br><img src="https://1013399.github.io/image-2/81/1068692021_l.jpg" alt="鵝公髻山東北峰、鵝公髻山" loading="lazy"></p><p>→經過大石壁<br><img src="https://1013399.github.io/image-2/81/1068691540_l.jpg" alt="鵝公髻山東北峰、鵝公髻山" loading="lazy"></p><p>→接上水泥農路，搭山友張先生的便車，回到第一登山口停車場<br><img src="https://1013399.github.io/image-2/81/1068693841_l.jpg" alt="鵝公髻山東北峰、鵝公髻山" loading="lazy"></p><h2 id="走走資訊" tabindex="-1"><a class="header-anchor" href="#走走資訊" aria-hidden="true">#</a> 走走資訊</h2><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>走走時間：約4.5小時(上山2.5小時、下山1.5小時、休息午餐0.5小時)</p><p>高低落差：約700公尺</p></div><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2>',53),c={href:"http://www.tonyhuang39.com/tony0638/tony0638.html",target:"_blank",rel:"noopener noreferrer"},d=i("br",null,null,-1),_=i("img",{src:"https://1013399.github.io/image-2/81/1068691541_l.jpg",alt:"鵝公髻山東北峰、鵝公髻山",loading:"lazy"},null,-1);function u(y,j){const a=l("ExternalLinkIcon");return p(),o("div",null,[n,m,r(" more "),b,i("p",null,[t("原圖連結→"),i("a",c,[t("Tony的自然人文旅記"),s(a)]),d,_])])}const x=g(h,[["render",u],["__file","post-81-2015-01-27.html.vue"]]);export{x as default};
