import{_ as a}from"./plugin-vue_export-helper-c27b6911.js";import{o as t,c as g,a as l,b as i,f as p,e as r}from"./app-7f62a6ff.js";const o={},s=i("p",null,[i("strong",null,"北門水晶教堂"),p("，由雲嘉南濱海國家風景區管理處規畫打造，面積達7公頃，原為吸引婚紗業者取景而興建，營造彷彿國外的特殊教堂造型和浪漫氣氛，尚未完工便已在網路及各媒體造成轟動，名列網路社群平台FLICKR 103年最夯、暴紅新興景點第一名，已成為網友最愛拍照分享景點之一。")],-1),e=r('<p>走走日期：2015/01/03</p><h2 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h2><p>國道1號南下，於下營系統交流道轉台84快速道路往北門方向，在台84北門出口(台17線)出交流道，接台17線左轉往北門，由於假日人潮多，北門遊客中心停車場早已客滿，約在台17線142.1Km處，便被交通警察引導至「頭港大排」旁土石產道臨時停車場停車。</p><h2 id="走走路線" tabindex="-1"><a class="header-anchor" href="#走走路線" aria-hidden="true">#</a> 走走路線</h2><p>→頭港大排臨時停車場，由北往南走，需步行約1.5公里才能到達水晶教堂<br><img src="https://1013399.github.io/image-2/85/1065812526_l.jpg" alt="水晶教堂" loading="lazy"></p><p>→北門嶼基督教會，曾經擔負烏腳病醫療與傳教功能<br><img src="https://1013399.github.io/image-2/85/1065809273_l.jpg" alt="水晶教堂" loading="lazy"></p><p>→烏腳病文化紀念館，記錄當時烏腳病的歷史與王金河醫師的奉獻事蹟<br><img src="https://1013399.github.io/image-2/85/1065810487_l.jpg" alt="水晶教堂" loading="lazy"></p><p>→永隆宮，主祀溫府千歲和廣澤尊王，為北門地區最重要的信仰中心之一<br><img src="https://1013399.github.io/image-2/85/1065812729_l.jpg" alt="水晶教堂" loading="lazy"></p><p>→北門嶼輕食風味餐廳，由廢棄的衛生所改建，旁邊為雲嘉南濱海國家風景區管理處<br><img src="https://1013399.github.io/image-2/85/1065811164_l.jpg" alt="水晶教堂" loading="lazy"></p><p>→錢來也雜貨店，為早期台鹽鹽工福利社，曾經是偶像劇的場景<br><img src="https://1013399.github.io/image-2/85/1065807909_l.jpg" alt="水晶教堂" loading="lazy"><br><img src="https://1013399.github.io/image-2/85/1065810854_l.jpg" alt="水晶教堂" loading="lazy"><br><img src="https://1013399.github.io/image-2/85/1065812926_l.jpg" alt="水晶教堂" loading="lazy"></p><p>→北門婚紗美地，濃厚的希臘風，遊客爭相拍照的景點<br><img src="https://1013399.github.io/image-2/85/1065810669_l.jpg" alt="水晶教堂" loading="lazy"><br><img src="https://1013399.github.io/image-2/85/1065810671_l.jpg" alt="水晶教堂" loading="lazy"><br><img src="https://1013399.github.io/image-2/85/1065811940_l.jpg" alt="水晶教堂" loading="lazy"><br><img src="https://1013399.github.io/image-2/85/1065810490_l.jpg" alt="水晶教堂" loading="lazy"><br><img src="https://1013399.github.io/image-2/85/1065809971_l.jpg" alt="水晶教堂" loading="lazy"><br><img src="https://1013399.github.io/image-2/85/1065810500_l.jpg" alt="水晶教堂" loading="lazy"><br><img src="https://1013399.github.io/image-2/85/1065812931_l.jpg" alt="水晶教堂" loading="lazy"><br><img src="https://1013399.github.io/image-2/85/1065811073_l.jpg" alt="水晶教堂" loading="lazy"><br><img src="https://1013399.github.io/image-2/85/1065810376_l.jpg" alt="水晶教堂" loading="lazy"><br><img src="https://1013399.github.io/image-2/85/1065812270_l.jpg" alt="水晶教堂" loading="lazy"></p><p>→北門遊客中心，由昔日北門洗滌鹽工廠倉庫改建<br><img src="https://1013399.github.io/image-2/85/1065810373_l.jpg" alt="水晶教堂" loading="lazy"></p><p>→北門遊客中心鎮館之寶，抹香鯨標本，長14M、高2.7M<br><img src="https://1013399.github.io/image-2/85/1065810498_l.jpg" alt="水晶教堂" loading="lazy"></p><p>→主題介紹區與多媒體互動區<br><img src="https://1013399.github.io/image-2/85/1065810499_l.jpg" alt="水晶教堂" loading="lazy"></p><p>→水晶教堂，參考澳洲雪梨歌劇院、關島海之教會教堂而建<br><img src="https://1013399.github.io/image-2/85/1065808975_l.jpg" alt="水晶教堂" loading="lazy"></p><p>→折返，回到雲嘉南濱海國家風景區管理處<br><img src="https://1013399.github.io/image-2/85/1065809075_l.jpg" alt="水晶教堂" loading="lazy"></p><p>→白色樂章浪漫藝術裝置區，各式白色樂器藝術地景裝置在管理處旁草地上<br><img src="https://1013399.github.io/image-2/85/1065809561_l.jpg" alt="水晶教堂" loading="lazy"></p><p>→夢奇地，是小朋友的樂園<br><img src="https://1013399.github.io/image-2/85/1065811090_l.jpg" alt="水晶教堂" loading="lazy"></p><p>→北門出張所，位於管理處後方<br><img src="https://1013399.github.io/image-2/85/1065808585_l.jpg" alt="水晶教堂" loading="lazy"><br><img src="https://1013399.github.io/image-2/85/1065812751_l.jpg" alt="水晶教堂" loading="lazy"></p><p>→穿越藝術裝置區，回到北門嶼基督教會，原路回停車處</p><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2><p>(取自Google地圖)<br><img src="https://1013399.github.io/image-2/85/1065810709_l.jpg" alt="水晶教堂" loading="lazy"></p>',22);function h(m,b){return t(),g("div",null,[s,l(" more "),e])}const d=a(o,[["render",h],["__file","post-85-2015-01-14.html.vue"]]);export{d as default};
