import{_ as a}from"./plugin-vue_export-helper-c27b6911.js";import{r as l,o as p,c as o,a as r,b as i,f as t,d as s,e as h}from"./app-afda0674.js";const e={},m=i("p",null,[i("strong",null,"白鷺鷥山"),t("，海拔143公尺，位於台北市內湖區大湖公園旁，大湖舊名十四分陂，故也稱十四分陂山，山頂有顆土地調查局圖根點，白鷺鷥山親山步道長約1.8Km，主要登山口，一在金湖路268號旁，另一則在成功路五段35巷，全程舖設石階步道。")],-1),b=i("p",null,[i("strong",null,"康樂山"),t("，海拔128公尺，位於台北市內湖區，有顆都市計劃處四等三角點No.114，山頂可眺望東湖地區、南港山、台北101大樓，康樂山親山步道長約1.5Km，主要登山口在成功路五段350巷口(黃石公廟對面)，以及安泰街水源頭福德宮旁。")],-1),n=h('<p><strong>柿子山</strong>，又名明舉山，海拔101公尺，位於台北市內湖區，山頂有顆北市三角點第74號，林中無視野，明舉山親山步道都是傳統泥土山徑，沒有鋪設石階，長約1.5Km，入口有安泰街登山口及康樂街的內溝溪生態館。</p><p>今日將車停於白鷺鷥山金湖路登山口旁，串連走走白鷺鷥山、康樂山、明舉山三段親山步道，最後下至內溝溪生態展示館，踢約1Km馬路至忠三街公車總站，搭287公車至捷運葫洲站下車，再步行約0.8Km馬路回到停車處，完成O形一圈。</p><h2 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h2><p>國道1號北上，下東湖交流道往內湖方向(康寧路)，過成功路後直行金湖路，抵達白鷺鷥山親山步道金湖路登山口，於附近道路旁未畫紅線處停車。</p><p>走走日期：2014/11/23</p><h2 id="走走路線" tabindex="-1"><a class="header-anchor" href="#走走路線" aria-hidden="true">#</a> 走走路線</h2><p>→白鷺鷥山親山步道金湖路入口，旁有步道指示牌<br><img src="https://1013399.github.io/image-2/89/1059734713_l.jpg" alt="白鷺鷥山" loading="lazy"></p><p>→白鷺鷥山親山步道，金湖路登山口，取右進入石階步道<br><img src="https://1013399.github.io/image-2/89/1059736869_l.jpg" alt="白鷺鷥山" loading="lazy"></p><p>→涼亭<br><img src="https://1013399.github.io/image-2/89/1059737388_l.jpg" alt="白鷺鷥山" loading="lazy"></p><p>→叉路，途中有多處類似之截彎取直捷徑，續行石階<br><img src="https://1013399.github.io/image-2/89/1059739563_l.jpg" alt="白鷺鷥山" loading="lazy"></p><p>→步道呈之字形往上<br><img src="https://1013399.github.io/image-2/89/1059738993_l.jpg" alt="白鷺鷥山" loading="lazy"></p><p>→途中展望<br><img src="https://1013399.github.io/image-2/89/1059734719_l.jpg" alt="白鷺鷥山" loading="lazy"></p><p>→叉路，右下可往成功路，往左續行石階<br><img src="https://1013399.github.io/image-2/89/1059740088_l.jpg" alt="白鷺鷥山" loading="lazy"></p><p>→涼棚觀景處<br><img src="https://1013399.github.io/image-2/89/1059738999_l.jpg" alt="白鷺鷥山" loading="lazy"></p><p>→展望不錯，下方為大湖公園<br><img src="https://1013399.github.io/image-2/89/1059740559_l.jpg" alt="白鷺鷥山" loading="lazy"></p><p>→叉路，右下可接石階步道往成功路，續往前行往基石處<br><img src="https://1013399.github.io/image-2/89/1059739883_l.jpg" alt="白鷺鷥山" loading="lazy"></p><p>→白鷺鷥山山頂平台<br><img src="https://1013399.github.io/image-2/89/1059738885_l.jpg" alt="白鷺鷥山" loading="lazy"></p><p>→白鷺鷥山，土地調查局圖根點<br><img src="https://1013399.github.io/image-2/89/1059740361_l.jpg" alt="白鷺鷥山" loading="lazy"></p><p>→續往前行下石階<br><img src="https://1013399.github.io/image-2/89/1059739571_l.jpg" alt="白鷺鷥山" loading="lazy"></p><p>→一路之字形往下，此段較陡，又稱五十彎步道<br><img src="https://1013399.github.io/image-2/89/1059740283_l.jpg" alt="白鷺鷥山" loading="lazy"></p><p>→佛字大石<br><img src="https://1013399.github.io/image-2/89/1059740751_l.jpg" alt="佛字大石" loading="lazy"></p><p>→這隻應該是鷺科的水鳥，立於路旁，完全不怕人<br><img src="https://1013399.github.io/image-2/89/1059734727_l.jpg" alt="鷺科的水鳥" loading="lazy"></p><p>→到達白鷺鷥山親山步道成功路入口<br><img src="https://1013399.github.io/image-2/89/1059734732_l.jpg" alt="白鷺鷥山" loading="lazy"></p><p>→入口處老公祠與福佑宮<br><img src="https://1013399.github.io/image-2/89/1059740754_l.jpg" alt="白鷺鷥山" loading="lazy"></p><p>→續行往成功路，穿越馬路後取右行<br><img src="https://1013399.github.io/image-2/89/1059738372_l.jpg" alt="白鷺鷥山" loading="lazy"></p><p>→康樂山親山步道登山口，位於成功路五段350巷口，進入巷內<br><img src="https://1013399.github.io/image-2/89/1059739785_l.jpg" alt="康樂山" loading="lazy"></p><p>→連續陡上石階<br><img src="https://1013399.github.io/image-2/89/1059740652_l.jpg" alt="康樂山" loading="lazy"></p><p>→經過菜園<br><img src="https://1013399.github.io/image-2/89/1059739694_l.jpg" alt="康樂山" loading="lazy"></p><p>→已經荒廢的山海寺<br><img src="https://1013399.github.io/image-2/89/1059740285_l.jpg" alt="康樂山 山海寺" loading="lazy"></p><p>→山海寺旁的大橄欖樹<br><img src="https://1013399.github.io/image-2/89/1059740757_l.jpg" alt="康樂山 山海寺" loading="lazy"></p><p>→過了山海寺，步道轉為泥土山徑<br><img src="https://1013399.github.io/image-2/89/1059735736_l.jpg" alt="康樂山" loading="lazy"></p><p>→叉路，右往康寧醫院，取左往康樂山<br><img src="https://1013399.github.io/image-2/89/1059738271_l.jpg" alt="康樂山" loading="lazy"></p><p>→叉路，左往菜園，取右續行<br><img src="https://1013399.github.io/image-2/89/1059739295_l.jpg" alt="康樂山" loading="lazy"></p><p>→步道往下<br><img src="https://1013399.github.io/image-2/89/1059737047_l.jpg" alt="康樂山" loading="lazy"></p><p>→過了里程500m後陡上<br><img src="https://1013399.github.io/image-2/89/1059740661_l.jpg" alt="康樂山" loading="lazy"></p><p>→山頭觀景點<br><img src="https://1013399.github.io/image-2/89/1059737988_l.jpg" alt="康樂山" loading="lazy"></p><p>→旁邊有顆無名基石<br><img src="https://1013399.github.io/image-2/89/1059739486_l.jpg" alt="康樂山" loading="lazy"></p><p>→觀景點展望佳，俯瞰大湖公園<br><img src="https://1013399.github.io/image-2/89/1059740100_l.jpg" alt="康樂山 俯瞰大湖公園" loading="lazy"></p><p>→叉路，左下往安泰街、柿子山，直行往康湖路、康樂山，先直行登康樂山再回<br><img src="https://1013399.github.io/image-2/89/1059739389_l.jpg" alt="康樂山 柿子山" loading="lazy"></p><p>→先下坡後上坡，即到達康樂山山頂<br><img src="https://1013399.github.io/image-2/89/1059740574_l.jpg" alt="康樂山山頂" loading="lazy"></p><p>→康樂山山頂展望，眺望東湖地區、南港山、台北101大樓<br><img src="https://1013399.github.io/image-2/89/1059740952_l.jpg" alt="康樂山山頂 眺望東湖地區、南港山、台北101大樓" loading="lazy"></p><p>→康樂山，都市計劃處四等三角點No.114，休息後原路折返<br><img src="https://1013399.github.io/image-2/89/1059738390_l.jpg" alt="康樂山" loading="lazy"></p><p>→回到前叉路，取右續行<br><img src="https://1013399.github.io/image-2/89/1059738892_l.jpg" alt="康樂山" loading="lazy"></p><p>→連續下坡，到達水源頭福德宮<br><img src="https://1013399.github.io/image-2/89/1059737466_l.jpg" alt="水源頭福德宮" loading="lazy"></p><p>→水源頭福德宮<br><img src="https://1013399.github.io/image-2/89/1059739302_l.jpg" alt="水源頭福德宮" loading="lazy"></p><p>→接安泰路，取右續行<br><img src="https://1013399.github.io/image-2/89/1059739391_l.jpg" alt="安泰路" loading="lazy"></p><p>→左上階梯應該是往聖明宮<br><img src="https://1013399.github.io/image-2/89/1059738391_l.jpg" alt="康樂山" loading="lazy"></p><p>→叉路，旁有土地公與龍虎亭，左往青山岩、牛稠湖尾山，取右續行安泰街<br><img src="https://1013399.github.io/image-2/89/1059738894_l.jpg" alt="康樂山" loading="lazy"></p><p>→明舉山親山步道安泰街登山口，旁有打印台<br><img src="https://1013399.github.io/image-2/89/1059739304_l.jpg" alt="明舉山親山步道安泰街登山口" loading="lazy"></p><p>→取左進入陡上泥土山徑<br><img src="https://1013399.github.io/image-2/89/1059736308_l.jpg" alt="柿子山(明舉山)" loading="lazy"></p><p>→進入平緩竹林<br><img src="https://1013399.github.io/image-2/89/1059737468_l.jpg" alt="柿子山(明舉山)" loading="lazy"></p><p>→叉路，直行往康樂街，先取左往柿子山<br><img src="https://1013399.github.io/image-2/89/1059737560_l.jpg" alt="柿子山(明舉山)" loading="lazy"></p><p>→柿子山(明舉山)山頂<br><img src="https://1013399.github.io/image-2/89/1059738393_l.jpg" alt="柿子山(明舉山)山頂" loading="lazy"></p><p>→柿子山，北市三角點第74號，樹林中無展望，原路折返<br><img src="https://1013399.github.io/image-2/89/1059739709_l.jpg" alt="柿子山(明舉山)" loading="lazy"></p><p>→回到前叉路，取左續行<br><img src="https://1013399.github.io/image-2/89/1059740772_l.jpg" alt="柿子山(明舉山)" loading="lazy"></p><p>→明舉山步道上下起伏較多，但幅度不大<br><img src="https://1013399.github.io/image-2/89/1059739011_l.jpg" alt="柿子山(明舉山)" loading="lazy"></p><p>→木椅，於此休息<br><img src="https://1013399.github.io/image-2/89/1059739093_l.jpg" alt="柿子山(明舉山)" loading="lazy"></p><p>→續行，開始陡下<br><img src="https://1013399.github.io/image-2/89/1059739094_l.jpg" alt="柿子山(明舉山)" loading="lazy"></p><p>→叉路，左右皆可往康樂街內溝溪生態展示館<br><img src="https://1013399.github.io/image-2/89/1059736318_l.jpg" alt="柿子山(明舉山)" loading="lazy"></p><p>→取左下石階<br><img src="https://1013399.github.io/image-2/89/1059740310_l.jpg" alt="柿子山(明舉山)" loading="lazy"></p><p>→下抵康樂街，左往海蝕岩壁，取右續行<br><img src="https://1013399.github.io/image-2/89/1059737113_l.jpg" alt="柿子山(明舉山)" loading="lazy"></p><p>→經過內溝溪生態展示館(往回拍攝)<br><img src="https://1013399.github.io/image-2/89/1059739714_l.jpg" alt="內溝溪生態展示館" loading="lazy"></p><p>→叉路，左為明山橋往北天宮，取右往東湖<br><img src="https://1013399.github.io/image-2/89/1059740584_l.jpg" alt="柿子山(明舉山)" loading="lazy"></p><p>→忠三街公車總站，搭287公車至捷運葫洲站下車，再步行回金湖路停車處<br>   P.S.搭欣欣客運646公車可回金湖路，但一天只有4班，且例假日停駛<br><img src="https://1013399.github.io/image-2/89/1059739715_l.jpg" alt="忠三街公車總站" loading="lazy"></p><h2 id="走走資訊" tabindex="-1"><a class="header-anchor" href="#走走資訊" aria-hidden="true">#</a> 走走資訊</h2><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>走走時間：約3小時(含休息，不含踢馬路及搭公車時間)</p><p>高低落差：(不含上下起伏)<br> 白鷺鷥山約110公尺、康樂山約110公尺、柿子山約90公尺</p></div><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2>',67),c={href:"http://www.tcge.taipei.gov.tw/ct.asp?xItem=37483059&ctNode=67807&mp=106051",target:"_blank",rel:"noopener noreferrer"},d=i("br",null,null,-1),_=i("img",{src:"https://1013399.github.io/image-2/89/1059987462_l.jpg",alt:"白鷺鷥山、康樂山、柿子山 參考地圖",loading:"lazy"},null,-1);function u(j,y){const g=l("ExternalLinkIcon");return p(),o("div",null,[m,b,r(" more "),n,i("p",null,[t("原圖連結→"),i("a",c,[t("臺北市親山步道主題網"),s(g)]),d,_])])}const x=a(e,[["render",u],["__file","post-89-2014-11-27.html.vue"]]);export{x as default};
