import{_ as g}from"./plugin-vue_export-helper-c27b6911.js";import{r as l,o as p,c as o,a as r,b as i,f as t,d as s,e}from"./app-c5577115.js";const h={},n=i("p",null,[i("strong",null,"石門山"),t("，又名小竹坑山，海拔551公尺，位於龍潭石門水庫旁，有顆一等三角點及一顆板新給水廠水源基石，附近另有一顆殖產局三角補點，山頂展望極佳，可俯瞰龍潭地區，該山名列為台灣小百岳No.24。")],-1),m=i("p",null,[i("strong",null,"太平山"),t("，海拔535公尺，位於石門山東方，山頂有內政部土地測量局四等控制點及通信鐵塔，山頂展望亦佳。"),i("br"),t("     石門山有多處入口，主要登山口為石門山牌樓(北入口)、小粗坑古道(南入口)及勞工育樂中心，今日再由北入口起登，輕鬆走走石門山。")],-1),b=e('<h2 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h2><p>國道3號北上，下關西交流道往關西方向，接台3線後北上，於台3線約53.8K處右轉台3乙線(龍源路)往石門水庫方向，於台3乙8.2K處再右轉民治路(桃63鄉道)往石門水庫，約行800公尺即抵達石門山北入口，於路旁停車，附近攤販林立，頗為熱鬧。</p><p>走走日期： 2014/11/01</p><h2 id="走走路線" tabindex="-1"><a class="header-anchor" href="#走走路線" aria-hidden="true">#</a> 走走路線</h2><p>→石門山牌樓(北入口)<br><img src="https://1013399.github.io/image-2/90/1057611156_l.jpg" alt="太平山、石門山" loading="lazy"></p><p>→進入寬廣泥土山路<br><img src="https://1013399.github.io/image-2/90/1057612733_l.jpg" alt="太平山、石門山" loading="lazy"></p><p>→豆花舖叉路，右側有福德祠，右可往勞工育樂中心，取左行<br><img src="https://1013399.github.io/image-2/90/1057611378_l.jpg" alt="太平山、石門山" loading="lazy"></p><p>→行約數公尺遇叉路，左水泥路往好漢坡，取右春景步道<br><img src="https://1013399.github.io/image-2/90/1057611157_l.jpg" alt="太平山、石門山" loading="lazy"></p><p>→進入泥土山徑、陡上<br><img src="https://1013399.github.io/image-2/90/1057614002_l.jpg" alt="太平山、石門山" loading="lazy"></p><p>→叉路，殊途同歸，取右上<br><img src="https://1013399.github.io/image-2/90/1057614195_l.jpg" alt="太平山、石門山" loading="lazy"></p><p>→連續陡上<br><img src="https://1013399.github.io/image-2/90/1057613422_l.jpg" alt="太平山、石門山" loading="lazy"><br><img src="https://1013399.github.io/image-2/90/1057613321_l.jpg" alt="太平山、石門山" loading="lazy"></p><p>→叉路，前一叉路於此相會，再取右上<br><img src="https://1013399.github.io/image-2/90/1057611379_l.jpg" alt="太平山、石門山" loading="lazy"></p><p>→依舊陡上<br><img src="https://1013399.github.io/image-2/90/1057614287_l.jpg" alt="太平山、石門山" loading="lazy"></p><p>→叉路，左往好漢坡，續取右上<br><img src="https://1013399.github.io/image-2/90/1057613120_l.jpg" alt="太平山、石門山" loading="lazy"></p><p>→繼續陡上<br><img src="https://1013399.github.io/image-2/90/1057611380_l.jpg" alt="太平山、石門山" loading="lazy"></p><p>→至稜線鞍部，叉路，右往石門山，先取左往太平山<br><img src="https://1013399.github.io/image-2/90/1057611448_l.jpg" alt="太平山、石門山" loading="lazy"></p><p>→叉路，右平緩繞腰路往好漢坡，取左上<br><img src="https://1013399.github.io/image-2/90/1057612846_l.jpg" alt="太平山、石門山" loading="lazy"></p><p>→陡上，但不長<br><img src="https://1013399.github.io/image-2/90/1057613121_l.jpg" alt="太平山、石門山" loading="lazy"></p><p>→太平山山頂<br><img src="https://1013399.github.io/image-2/90/1057610866_l.jpg" alt="太平山、石門山" loading="lazy"></p><p>→山頂視野佳<br><img src="https://1013399.github.io/image-2/90/1057613423_l.jpg" alt="太平山、石門山" loading="lazy"></p><p>→太平山，內政部土地測量局四等控制點<br><img src="https://1013399.github.io/image-2/90/1057613631_l.jpg" alt="太平山、石門山" loading="lazy"></p><p>→往前行不遠，有座通信鐵塔<br><img src="https://1013399.github.io/image-2/90/1057611450_l.jpg" alt="太平山、石門山" loading="lazy"></p><p>→續往前行，可往好漢坡，原路折返<br><img src="https://1013399.github.io/image-2/90/1057612735_l.jpg" alt="太平山、石門山" loading="lazy"></p><p>→回到稜線鞍部叉路，取直往石門山<br><img src="https://1013399.github.io/image-2/90/1057611381_l.jpg" alt="太平山、石門山" loading="lazy"></p><p>→平緩的稜線<br><img src="https://1013399.github.io/image-2/90/1057610867_l.jpg" alt="太平山、石門山" loading="lazy"></p><p>→休憩空地，樹上釘牌名為「市外桃源」<br><img src="https://1013399.github.io/image-2/90/1057613715_l.jpg" alt="太平山、石門山" loading="lazy"></p><p>→十字叉路，左往奉天宮，右往勞工育樂中心，續直行往石門山<br><img src="https://1013399.github.io/image-2/90/1057611382_l.jpg" alt="太平山、石門山" loading="lazy"></p><p>→拉繩陡上<br><img src="https://1013399.github.io/image-2/90/1057614004_l.jpg" alt="太平山、石門山" loading="lazy"></p><p>→叉路，右可往觀音菩薩像，取左<br><img src="https://1013399.github.io/image-2/90/1057611453_l.jpg" alt="太平山、石門山" loading="lazy"></p><p>→繼續陡上<br><img src="https://1013399.github.io/image-2/90/1057613426_l.jpg" alt="太平山、石門山" loading="lazy"></p><p>→上至山頭，右側有叉路可往觀音菩薩像，續直行<br><img src="https://1013399.github.io/image-2/90/1057612999_l.jpg" alt="太平山、石門山" loading="lazy"></p><p>→石門山山頂<br><img src="https://1013399.github.io/image-2/90/1057613126_l.jpg" alt="太平山、石門山" loading="lazy"></p><p>→石門山，一等三角點及板新給水廠水源基石<br><img src="https://1013399.github.io/image-2/90/1057614289_l.jpg" alt="太平山、石門山" loading="lazy"></p><p>→往前續行，便可見石門山殖產局三角補點<br><img src="https://1013399.github.io/image-2/90/1057613520_l.jpg" alt="太平山、石門山" loading="lazy"></p><p>→俯瞰龍潭地區、休息，續行可往南入口，原路折返<br><img src="https://1013399.github.io/image-2/90/1057613427_l.jpg" alt="太平山、石門山" loading="lazy"></p><p>→回到十字叉路，取左下往勞工育樂中心<br><img src="https://1013399.github.io/image-2/90/1057612738_l.jpg" alt="太平山、石門山" loading="lazy"></p><p>→連續陡下<br><img src="https://1013399.github.io/image-2/90/1057611458_l.jpg" alt="太平山、石門山" loading="lazy"></p><p>→叉路，左上往觀音菩薩像，續直行往下<br><img src="https://1013399.github.io/image-2/90/1057613717_l.jpg" alt="太平山、石門山" loading="lazy"></p><p>→接上水泥路<br><img src="https://1013399.github.io/image-2/90/1057613718_l.jpg" alt="太平山、石門山" loading="lazy"></p><p>→右側為「玉樹草堂」三合院<br><img src="https://1013399.github.io/image-2/90/1057613521_l.jpg" alt="太平山、石門山" loading="lazy"></p><p>→沿途有許多卡拉OK餐廳<br><img src="https://1013399.github.io/image-2/90/1057611066_l.jpg" alt="太平山、石門山" loading="lazy"></p><p>→叉路，直行往勞工育樂中心，取右回<br><img src="https://1013399.github.io/image-2/90/1057613428_l.jpg" alt="太平山、石門山" loading="lazy"></p><p>→經過福德宮<br><img src="https://1013399.github.io/image-2/90/1057614200_l.jpg" alt="太平山、石門山" loading="lazy"></p><p>→回到豆花舖叉路，取左回<br><img src="https://1013399.github.io/image-2/90/1057614388_l.jpg" alt="太平山、石門山" loading="lazy"></p><p>→回到石門山牌樓<br><img src="https://1013399.github.io/image-2/90/1057614201_l.jpg" alt="太平山、石門山" loading="lazy"></p><h2 id="走走資訊" tabindex="-1"><a class="header-anchor" href="#走走資訊" aria-hidden="true">#</a> 走走資訊</h2><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>走走時間：約2.5小時(含休息)</p><p>高低落差：約300公尺</p></div><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2>',48),c=i("br",null,null,-1),d={href:"http://www.tonyhuang39.com/tony0452/tony0452.html",target:"_blank",rel:"noopener noreferrer"},_=i("br",null,null,-1),u=i("img",{src:"https://1013399.github.io/image-2/90/1057614391_l.jpg",alt:"太平山、石門山 參考地圖",loading:"lazy"},null,-1);function y(j,z){const a=l("ExternalLinkIcon");return p(),o("div",null,[n,m,r(" more "),b,i("p",null,[t("(修改過原圖，紅色為此次走走路線)"),c,t(" 原圖連結→"),i("a",d,[t("Tony的自然人文旅記"),s(a)]),_,u])])}const N=g(h,[["render",y],["__file","post-90-2014-11-11.html.vue"]]);export{N as default};
