import{_ as o}from"./plugin-vue_export-helper-c27b6911.js";import{r as g,o as r,c as e,a as s,b as i,d as a,w as h,e as t,f as n}from"./app-455fc4b3.js";const m={},b=i("h2",{id:"實際行程",tabindex:"-1"},[i("a",{class:"header-anchor",href:"#實際行程","aria-hidden":"true"},"#"),t(" 實際行程")],-1),c=i("p",null,"昨夜排雲山莊19:00就寢→凌晨02:00起床→02:30早早餐、整理背包(輕裝)→03:00出發(當時氣溫：3℃)→04:40玉山主峰登頂、等候日出(需注意保暖)→07:00下山→08:30回到排雲山莊→09:10早午餐→09:40回程→12:40回到塔塔加鞍部",-1),d=n('<p>走走日期：2014/10/25</p><h2 id="走走路線" tabindex="-1"><a class="header-anchor" href="#走走路線" aria-hidden="true">#</a> 走走路線</h2><p>→帶頭燈出發，登山口在室外廁所後方<br><img src="https://1013399.github.io/image-2/91/1055842235_l.jpg" alt="塔塔加上玉山主峰 玉山主峰" loading="lazy"></p><p>→經過直升機吊掛平台<br><img src="https://1013399.github.io/image-2/91/1055847557_l.jpg" alt="塔塔加上玉山主峰 玉山主峰" loading="lazy"></p><p>→之字型陡上<br><img src="https://1013399.github.io/image-2/91/1055847657_l.jpg" alt="塔塔加上玉山主峰 玉山主峰" loading="lazy"></p><p>→圓峰叉路，右可往圓峰山屋，取左往主峰<br><img src="https://1013399.github.io/image-2/91/1055847757_l.jpg" alt="塔塔加上玉山主峰 玉山主峰" loading="lazy"></p><p>→1K里程牌<br><img src="https://1013399.github.io/image-2/91/1055842616_l.jpg" alt="塔塔加上玉山主峰 玉山主峰" loading="lazy"></p><p>→碎石崩塌區<br><img src="https://1013399.github.io/image-2/91/1055846460_l.jpg" alt="塔塔加上玉山主峰 玉山主峰" loading="lazy"></p><p>→1.5K里程牌<br><img src="https://1013399.github.io/image-2/91/1055842618_l.jpg" alt="塔塔加上玉山主峰 玉山主峰" loading="lazy"></p><p>→拉鐵鍊陡上<br><img src="https://1013399.github.io/image-2/91/1055846862_l.jpg" alt="塔塔加上玉山主峰 玉山主峰" loading="lazy"></p><p>→2K里程牌<br><img src="https://1013399.github.io/image-2/91/1055844595_l.jpg" alt="塔塔加上玉山主峰 玉山主峰" loading="lazy"></p><p>→經過明隧道<br><img src="https://1013399.github.io/image-2/91/1055845364_l.jpg" alt="塔塔加上玉山主峰 玉山主峰" loading="lazy"></p><p>→玉山北峰叉路，此處為風口，風很大、很冷，直行可往玉山北峰，取右往主峰<br><img src="https://1013399.github.io/image-2/91/1055847658_l.jpg" alt="塔塔加上玉山主峰 玉山主峰" loading="lazy"></p><p>→最後200公尺，高角度陡上攻頂<br><img src="https://1013399.github.io/image-2/91/1055846462_l.jpg" alt="塔塔加上玉山主峰 玉山主峰" loading="lazy"></p><p>→連續陡上，到達玉山主峰山頂<br><img src="https://1013399.github.io/image-2/91/1055845563_l.jpg" alt="塔塔加上玉山主峰 玉山主峰" loading="lazy"></p><p>→玉山主峰石碑<br><img src="https://1013399.github.io/image-2/91/1055847958_l.jpg" alt="塔塔加上玉山主峰 玉山主峰" loading="lazy"></p><p>→玉山主峰一等三角點<br><img src="https://1013399.github.io/image-2/91/1055846463_l.jpg" alt="塔塔加上玉山主峰 玉山主峰" loading="lazy"></p><p>→玉山主峰石碑與一等三角點基石<br><img src="https://1013399.github.io/image-2/91/1055846464_l.jpg" alt="塔塔加上玉山主峰 玉山主峰" loading="lazy"></p><p>→等候日出，東方天空已經開始漸漸泛紅<br><img src="https://1013399.github.io/image-2/91/1055846467_l.jpg" alt="塔塔加上玉山主峰 玉山主峰" loading="lazy"></p><p>→今日雲層稍多，太陽露臉時間稍晚<br><img src="https://1013399.github.io/image-2/91/1055846871_l.jpg" alt="塔塔加上玉山主峰 玉山主峰" loading="lazy"></p><p>→眺望玉山東峰<br><img src="https://1013399.github.io/image-2/91/1055848162_l.jpg" alt="塔塔加上玉山主峰 玉山主峰" loading="lazy"></p><p>→眺望玉山北峰，山頂有氣象站<br><img src="https://1013399.github.io/image-2/91/1055844600_l.jpg" alt="塔塔加上玉山主峰 玉山主峰" loading="lazy"></p><p>→別隊山友排隊輪流拍完照，開始陸續下山<br><img src="https://1013399.github.io/image-2/91/1055841721_l.jpg" alt="塔塔加上玉山主峰 玉山主峰" loading="lazy"></p><p>→主峰前面這顆石頭，像是守護玉山的石獅<br><img src="https://1013399.github.io/image-2/91/1055842514_l.jpg" alt="塔塔加上玉山主峰 玉山主峰" loading="lazy"></p><p>→與同行山友拍張合照<br><img src="https://1013399.github.io/image-2/91/1055842232_l.jpg" alt="塔塔加上玉山主峰 玉山主峰" loading="lazy"></p><p>→在山頂耗了兩個多小時，開始下山，我們是最早登頂、最晚下山<br><img src="https://1013399.github.io/image-2/91/1055844213_l.jpg" alt="塔塔加上玉山主峰 玉山主峰" loading="lazy"></p><p>→回到北峰叉路，取左回排雲山莊<br><img src="https://1013399.github.io/image-2/91/1055846670_l.jpg" alt="塔塔加上玉山主峰 玉山主峰" loading="lazy"></p><p>→叉路口眺望玉山北峰、北北峰<br><img src="https://1013399.github.io/image-2/91/1055848065_l.jpg" alt="塔塔加上玉山主峰 玉山主峰" loading="lazy"></p><p>→回到明隧道<br><img src="https://1013399.github.io/image-2/91/1055848264_l.jpg" alt="塔塔加上玉山主峰 玉山主峰" loading="lazy"></p><p>→俯瞰先行下山的山友行進於碎石坡間<br><img src="https://1013399.github.io/image-2/91/1055848657_l.jpg" alt="塔塔加上玉山主峰 玉山主峰" loading="lazy"></p><p>→回望玉山主峰<br><img src="https://1013399.github.io/image-2/91/1055846168_l.jpg" alt="塔塔加上玉山主峰 玉山主峰" loading="lazy"></p><p>→碎石坡兩側皆是玉山圓柏，前方山徑為往圓峰山屋的路徑<br><img src="https://1013399.github.io/image-2/91/1055844603_l.jpg" alt="塔塔加上玉山主峰 玉山主峰" loading="lazy"></p><p>→漸漸進入冷杉林<br><img src="https://1013399.github.io/image-2/91/1055848267_l.jpg" alt="塔塔加上玉山主峰 玉山主峰" loading="lazy"></p><p>→回到圓峰山屋叉路，取右回排雲山莊<br><img src="https://1013399.github.io/image-2/91/1055846169_l.jpg" alt="塔塔加上玉山主峰 玉山主峰" loading="lazy"></p><p>→此處大約是冷杉、玉山圓柏的交界，稱之為森林界線<br><img src="https://1013399.github.io/image-2/91/1055847765_l.jpg" alt="塔塔加上玉山主峰 玉山主峰" loading="lazy"></p><p>→再回望玉山主峰<br><img src="https://1013399.github.io/image-2/91/1055843502_l.jpg" alt="塔塔加上玉山主峰 玉山主峰" loading="lazy"></p><p>→之字型陡下<br><img src="https://1013399.github.io/image-2/91/1055845571_l.jpg" alt="塔塔加上玉山主峰 玉山主峰" loading="lazy"></p><p>→玉山杜鵑<br><img src="https://1013399.github.io/image-2/91/1055847460_l.jpg" alt="塔塔加上玉山主峰 玉山主峰" loading="lazy"></p><p>→回到排雲山莊直升機吊掛平台<br><img src="https://1013399.github.io/image-2/91/1055847767_l.jpg" alt="塔塔加上玉山主峰 玉山主峰" loading="lazy"></p><p>→回到排雲山莊，休息、整理背包，等候享用早午餐<br><img src="https://1013399.github.io/image-2/91/1055846974_l.jpg" alt="塔塔加上玉山主峰 玉山主峰" loading="lazy"></p><p>→吃完早午餐(麵條)後，告別莊主，開始回程</p><p>→陸續經過各個里程牌<br><img src="https://1013399.github.io/image-2/91/1055847869_l.jpg" alt="塔塔加上玉山主峰 玉山主峰" loading="lazy"></p><p>→回到5K處的觀景台，休息<br><img src="https://1013399.github.io/image-2/91/1055848168_l.jpg" alt="塔塔加上玉山主峰 玉山主峰" loading="lazy"></p><p>→往回仰望玉山主峰<br><img src="https://1013399.github.io/image-2/91/1055847673_l.jpg" alt="塔塔加上玉山主峰 玉山主峰" loading="lazy"></p><p>→續行，經過5K、4.5K...，回到0.5K<br><img src="https://1013399.github.io/image-2/91/1055843504_l.jpg" alt="塔塔加上玉山主峰 玉山主峰" loading="lazy"><br><img src="https://1013399.github.io/image-2/91/1055843505_l.jpg" alt="塔塔加上玉山主峰 玉山主峰" loading="lazy"></p><p>→回到塔塔加鞍部玉山登山口，搭接駁車回上東埔停車場<br><img src="https://1013399.github.io/image-2/91/1055843506_l.jpg" alt="塔塔加上玉山主峰 玉山主峰" loading="lazy"></p><h2 id="走走資訊" tabindex="-1"><a class="header-anchor" href="#走走資訊" aria-hidden="true">#</a> 走走資訊</h2><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>時間與落差：</p><ul><li>排雲山莊  至  玉山主峰    1小時40分鐘、約上升550公尺</li><li>玉山主峰  至  排雲山莊    1小時30分鐘、約下降550公尺</li><li>排雲山莊  至  塔塔加鞍部  3小時、約下降780公尺</li></ul></div><h2 id="上一篇相關文章" tabindex="-1"><a class="header-anchor" href="#上一篇相關文章" aria-hidden="true">#</a> 上一篇相關文章</h2>',49),_=i("h2",{id:"參考地圖",tabindex:"-1"},[i("a",{class:"header-anchor",href:"#參考地圖","aria-hidden":"true"},"#"),t(" 參考地圖")],-1),u={href:"http://www.sunriver.com.tw/map_hiking_index.htm",target:"_blank",rel:"noopener noreferrer"},y=i("br",null,null,-1),j=i("img",{src:"https://1013399.github.io/image-2/91/1055842632_l.jpg",alt:"塔塔加上玉山主峰 玉山主峰 參考地圖",loading:"lazy"},null,-1),z={href:"http://www.sunriver.com.tw/takayama/yushan.htm",target:"_blank",rel:"noopener noreferrer"},f=i("br",null,null,-1),x=i("img",{src:"https://1013399.github.io/image-2/91/1056841160_l.jpg",alt:"塔塔加上玉山主峰 玉山主峰 參考地圖",loading:"lazy"},null,-1);function k(w,v){const p=g("RouterLink"),l=g("ExternalLinkIcon");return r(),e("div",null,[b,c,s(" more "),d,i("p",null,[a(p,{to:"/posts/post-92-2014-11-05.html"},{default:h(()=>[t("【南投信義】塔塔加上玉山主峰(DAY1－排雲山莊)")]),_:1})]),_,i("p",null,[t("原圖連結→"),i("a",u,[t("上河文化－台灣高山步程示意圖"),a(l)]),y,j]),i("p",null,[t("原圖連結→"),i("a",z,[t("上河文化－臺灣百岳全集書摘"),a(l)]),f,x])])}const V=o(m,[["render",k],["__file","post-91-2014-11-06.html.vue"]]);export{V as default};
