import{_ as o}from"./plugin-vue_export-helper-c27b6911.js";import{r as g,o as r,c as e,a as s,b as i,d as a,w as h,f as t,e as n}from"./app-6513a715.js";const m={},b=i("p",null,[t("台灣第一高峰－"),i("strong",null,"玉山"),t("，日治時稱為新高山，海拔3952公尺，位於南投縣信義鄉、高雄市桃源區及嘉義縣阿里山鄉交界處，山頂有顆一等三角點及玉山主峰石碑，視野極佳，有360度展望，名列台灣百岳之首，山勢亦為五岳之首，攻頂不難，惟排雲山莊床位抽籤較難。")],-1),c=i("p",null,"應同事之邀，計畫第一天走至排雲山莊後，順便撿個玉山西峰，次日於玉山主峰看完日出後，再走個玉山北峰，但在入園申請時，未填入西、北峰，抵達排雲山莊，稍事休息後，準備出發前往西峰，莊主以未申請為由而不放行，並且囑咐明日也只能上主峰，不可違規前往北峰，因此，此次玉山之行，算是輕鬆之旅。",-1),d=n('<p>走走日期：2014/10/24</p><h2 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h2><p>因台21線(新中橫)122Km至145Km於每日17:30後管制禁止通行，故於前一日(10/23)下午出發。</p><p>國道3號下名間交流道往集集方向，經過集集、水里後接台21線往信義鄉，行至台21線142Km附近，停車參觀夫妻樹，再續行至台21線終點(145Km)塔塔加遊客中心後，接台18線續行至上東埔停車場停車，夜宿東埔山莊(每人300元)。</p><h2 id="走走路線" tabindex="-1"><a class="header-anchor" href="#走走路線" aria-hidden="true">#</a> 走走路線</h2><p>→東埔山莊出發<br><img src="https://1013399.github.io/image-2/92/1055842778_l.jpg" alt="塔塔加上玉山主峰 排雲山莊" loading="lazy"></p><p>→上東埔停車場，整裝<br><img src="https://1013399.github.io/image-2/92/1055843278_l.jpg" alt="塔塔加上玉山主峰 排雲山莊" loading="lazy"></p><p>→進入停車場對面的玉山林道<br><img src="https://1013399.github.io/image-2/92/1055839928_l.jpg" alt="塔塔加上玉山主峰 排雲山莊" loading="lazy"></p><p>→仰望鹿林前山<br><img src="https://1013399.github.io/image-2/92/1055842093_l.jpg" alt="塔塔加上玉山主峰 排雲山莊" loading="lazy"></p><p>→塔塔加小隊繳交入山證、排雲管理站繳交入園證及驗證，搭接駁車往玉山登山口(每人100元)<br><img src="https://1013399.github.io/image-2/92/1055840447_l.jpg" alt="塔塔加上玉山主峰 排雲山莊" loading="lazy"></p><p>→塔塔加鞍部玉山登山口，右側步道可上麟趾山、鹿林山<br><img src="https://1013399.github.io/image-2/92/1055838730_l.jpg" alt="塔塔加上玉山主峰 排雲山莊" loading="lazy"></p><p>→登山口路線圖<br><img src="https://1013399.github.io/image-2/92/1055842096_l.jpg" alt="塔塔加上玉山主峰 排雲山莊" loading="lazy"></p><p>→出發，步道平緩好走<br><img src="https://1013399.github.io/image-2/92/1055842097_l.jpg" alt="塔塔加上玉山主峰 排雲山莊" loading="lazy"></p><p>→回望層層山巒與雲海<br><img src="https://1013399.github.io/image-2/92/1055841997_l.jpg" alt="塔塔加上玉山主峰 排雲山莊" loading="lazy"></p><p>→經過落石區<br><img src="https://1013399.github.io/image-2/92/1055839933_l.jpg" alt="塔塔加上玉山主峰 排雲山莊" loading="lazy"></p><p>→回望玉山登山口與後方麟趾山<br><img src="https://1013399.github.io/image-2/92/1055839936_l.jpg" alt="塔塔加上玉山主峰 排雲山莊" loading="lazy"></p><p>→第一座棧橋，至排雲山莊共有82座木棧橋(每座有編號)<br><img src="https://1013399.github.io/image-2/92/1055838732_l.jpg" alt="塔塔加上玉山主峰 排雲山莊 第一座棧橋" loading="lazy"></p><p>→0.5K里程牌<br><img src="https://1013399.github.io/image-2/92/1055839938_l.jpg" alt="塔塔加上玉山主峰 排雲山莊" loading="lazy"></p><p>→之字型陡上<br><img src="https://1013399.github.io/image-2/92/1055840452_l.jpg" alt="塔塔加上玉山主峰 排雲山莊" loading="lazy"></p><p>→眺望麟趾山、鹿林山、鹿林前山<br><img src="https://1013399.github.io/image-2/92/1055839544_l.jpg" alt="塔塔加上玉山主峰 排雲山莊" loading="lazy"></p><p>→孟祿亭，紀念外國人孟祿先生，約在1.7K處，在此休息<br><img src="https://1013399.github.io/image-2/92/1055839939_l.jpg" alt="塔塔加上玉山主峰 排雲山莊" loading="lazy"></p><p>→不怕人的金翼白眉<br><img src="https://1013399.github.io/image-2/92/1055843657_l.jpg" alt="金翼白眉" loading="lazy"></p><p>→續行約50公尺，步道下方有座乾式廁所<br><img src="https://1013399.github.io/image-2/92/1055841686_l.jpg" alt="塔塔加上玉山主峰 排雲山莊" loading="lazy"></p><p>→步道平緩，欣賞高山美景<br><img src="https://1013399.github.io/image-2/92/1055841688_l.jpg" alt="塔塔加上玉山主峰 排雲山莊" loading="lazy"></p><p>→玉山前峰登山口，約在2.7K處，左陡上可往玉山前峰，續直行<br><img src="https://1013399.github.io/image-2/92/1055839548_l.jpg" alt="玉山前峰登山口" loading="lazy"></p><p>→道路崎嶇，但不難走<br><img src="https://1013399.github.io/image-2/92/1055842199_l.jpg" alt="塔塔加上玉山主峰 排雲山莊" loading="lazy"></p><p>→經過漂亮的冷杉、鐵杉林<br><img src="https://1013399.github.io/image-2/92/1055842003_l.jpg" alt="塔塔加上玉山主峰 排雲山莊" loading="lazy"></p><p>→4K里程牌，海拔已接近3000公尺了<br><img src="https://1013399.github.io/image-2/92/1055838743_l.jpg" alt="塔塔加上玉山主峰 排雲山莊" loading="lazy"></p><p>→回望玉山前峰<br><img src="https://1013399.github.io/image-2/92/1055838744_l.jpg" alt="玉山前峰" loading="lazy"></p><p>→到達5K的公廁及觀景台，後方為玉山西峰，休息、賞景、補充熱量<br><img src="https://1013399.github.io/image-2/92/1055843658_l.jpg" alt="塔塔加上玉山主峰 排雲山莊" loading="lazy"></p><p>→觀景台前賞白木林<br><img src="https://1013399.github.io/image-2/92/1055837906_l.jpg" alt="塔塔加上玉山主峰 白木林" loading="lazy"></p><p>→續行，回望觀景台<br><img src="https://1013399.github.io/image-2/92/1055842204_l.jpg" alt="塔塔加上玉山主峰 排雲山莊" loading="lazy"></p><p>→陡上，5K至5.5K為較陡的一段<br><img src="https://1013399.github.io/image-2/92/1055841212_l.jpg" alt="塔塔加上玉山主峰 排雲山莊" loading="lazy"></p><p>→步道趨緩，經過斷崖與落石路段，經過6K里程牌<br><img src="https://1013399.github.io/image-2/92/1055842205_l.jpg" alt="塔塔加上玉山主峰 排雲山莊" loading="lazy"><br><img src="https://1013399.github.io/image-2/92/1055842568_l.jpg" alt="塔塔加上玉山主峰 排雲山莊" loading="lazy"></p><p>→步道轉為往下行<br><img src="https://1013399.github.io/image-2/92/1055843091_l.jpg" alt="塔塔加上玉山主峰 排雲山莊" loading="lazy"></p><p>→大峭壁，約在6.7K處<br><img src="https://1013399.github.io/image-2/92/1055837333_l.jpg" alt="塔塔加上玉山主峰 排雲山莊" loading="lazy"></p><p>→步道趨陡，經過7K里程牌<br><img src="https://1013399.github.io/image-2/92/1055841691_l.jpg" alt="塔塔加上玉山主峰 排雲山莊" loading="lazy"></p><p>→玉山杜鵑與玉山圓柏<br><img src="https://1013399.github.io/image-2/92/1055841216_l.jpg" alt="玉山杜鵑與玉山圓柏" loading="lazy"></p><p>→繼續陡上，來到8K處<br><img src="https://1013399.github.io/image-2/92/1055843760_l.jpg" alt="塔塔加上玉山主峰 排雲山莊" loading="lazy"></p><p>→第82號棧橋，即將到達排雲山莊<br><img src="https://1013399.github.io/image-2/92/1055843577_l.jpg" alt="塔塔加上玉山主峰 排雲山莊" loading="lazy"></p><p>→可看見上方的排雲山莊了<br><img src="https://1013399.github.io/image-2/92/1055843578_l.jpg" alt="塔塔加上玉山主峰 排雲山莊" loading="lazy"></p><p>→經過箭竹林，轉為石階步道<br><img src="https://1013399.github.io/image-2/92/1055842293_l.jpg" alt="塔塔加上玉山主峰 排雲山莊" loading="lazy"></p><p>→到達排雲山莊<br><img src="https://1013399.github.io/image-2/92/1055840939_l.jpg" alt="塔塔加上玉山主峰 排雲山莊" loading="lazy"></p><p>→排雲山莊，休息，莊主檢查入園證、說明住宿規定<br><img src="https://1013399.github.io/image-2/92/1055839950_l.jpg" alt="塔塔加上玉山主峰 排雲山莊" loading="lazy"></p><p>→玉山西峰登山口，莊主在此把關，入園證未申請不放行，只好回房間睡午覺<br><img src="https://1013399.github.io/image-2/92/1055841484_l.jpg" alt="塔塔加上玉山主峰 排雲山莊" loading="lazy"></p><h2 id="走走資訊" tabindex="-1"><a class="header-anchor" href="#走走資訊" aria-hidden="true">#</a> 走走資訊</h2><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>走走時間：約5小時(含休息)</p><p>高低落差：約780公尺</p></div><h2 id="下一篇相關文章" tabindex="-1"><a class="header-anchor" href="#下一篇相關文章" aria-hidden="true">#</a> 下一篇相關文章</h2>',48),_=i("h2",{id:"參考地圖",tabindex:"-1"},[i("a",{class:"header-anchor",href:"#參考地圖","aria-hidden":"true"},"#"),t(" 參考地圖")],-1),u={href:"http://www.sunriver.com.tw/map_hiking_index.htm",target:"_blank",rel:"noopener noreferrer"},y=i("br",null,null,-1),j=i("img",{src:"https://1013399.github.io/image-2/92/1055842864_l.jpg",alt:"塔塔加上玉山主峰 排雲山莊 參考地圖",loading:"lazy"},null,-1),z={href:"http://www.sunriver.com.tw/takayama/yushan.htm",target:"_blank",rel:"noopener noreferrer"},f=i("br",null,null,-1),K=i("img",{src:"https://1013399.github.io/image-2/92/1056840766_l.jpg",alt:"塔塔加上玉山主峰 排雲山莊 參考地圖",loading:"lazy"},null,-1);function x(k,w){const p=g("RouterLink"),l=g("ExternalLinkIcon");return r(),e("div",null,[b,c,s(" more "),d,i("p",null,[a(p,{to:"/posts/post-91-2014-11-06.html"},{default:h(()=>[t("【南投信義】塔塔加上玉山主峰(DAY2－玉山主峰)")]),_:1})]),_,i("p",null,[t("原圖連結→"),i("a",u,[t("上河文化－台灣高山步程示意圖"),a(l)]),y,j]),i("p",null,[t("原圖連結→"),i("a",z,[t("上河文化－臺灣百岳全集書摘"),a(l)]),f,K])])}const V=o(m,[["render",x],["__file","post-92-2014-11-05.html.vue"]]);export{V as default};
