import{_ as l}from"./plugin-vue_export-helper-c27b6911.js";import{r as p,o as g,c as e,a as o,b as t,f as i,d as r,e as s}from"./app-c5577115.js";const h={},n=t("p",null,[t("strong",null,"石門山"),i("，海拔3237公尺，山頂有一顆三等三角點No.6389，為合歡群峰之一，因日治時代合歡越嶺道鑿開了這座山北稜的岩壁，形成一個石門而得名。石門山名列為台灣百岳No.66，山勢為八小巒之2，登頂僅需30分鐘，是最容易攻頂的百岳之一。")],-1),c=t("p",null,"因同事邀約下週同登台灣第一高峰－玉山，並已抽中排雲山莊住宿，今日特地來到合歡山附近適應高山海拔，原計畫走走石門山、合歡山東峰與合歡山主峰，因武嶺舉辦自行車活動，又逢假日塞車嚴重，時間不夠之下，只完成了最輕鬆的石門山，這也是個人的百岳初體驗。",-1),d=s('<p>走走日期：2014/10/19</p><h2 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h2><p>國道3號轉國道6號，至國道6號終點埔里，接埔霧公路(台14線)左轉往霧社，過了霧社轉台14甲往清境農場，經過清境農場後續行至台14甲33.5Km舊寒訓中心小停車場停車，石門山登山口在馬路對面，此處也是合歡尖山的第二登山口。</p><h2 id="走走路線" tabindex="-1"><a class="header-anchor" href="#走走路線" aria-hidden="true">#</a> 走走路線</h2><p>→台14甲33.5Km舊寒訓中心小停車場，後方即為合歡尖山<br><img src="https://1013399.github.io/image-2/93/1055835699_l.jpg" alt="合歡山 石門山" loading="lazy"></p><p>→馬路對面即為石門山登山口<br><img src="https://1013399.github.io/image-2/93/1055836969_l.jpg" alt="合歡山 石門山" loading="lazy"></p><p>→石門山步道OK<br><img src="https://1013399.github.io/image-2/93/1055835825_l.jpg" alt="合歡山 石門山" loading="lazy"></p><p>→步道口解說牌<br><img src="https://1013399.github.io/image-2/93/1055837260_l.jpg" alt="合歡山 石門山" loading="lazy"></p><p>→出發，緩上往石門山<br><img src="https://1013399.github.io/image-2/93/1055836871_l.jpg" alt="合歡山 石門山" loading="lazy"></p><p>→步道O.1K，步道稍陡，高山行走，必須慢行<br><img src="https://1013399.github.io/image-2/93/1055832536_l.jpg" alt="合歡山 石門山" loading="lazy"></p><p>→眺望奇萊北峰<br><img src="https://1013399.github.io/image-2/93/1055837959_l.jpg" alt="合歡山 石門山" loading="lazy"></p><p>→步道趨緩，穿梭於箭竹林間<br><img src="https://1013399.github.io/image-2/93/1055834331_l.jpg" alt="合歡山 石門山" loading="lazy"></p><p>→回望松雪樓，右側山頭為合歡東峰<br><img src="https://1013399.github.io/image-2/93/1055834332_l.jpg" alt="合歡山 石門山" loading="lazy"></p><p>→走不久便可看見山頂了<br><img src="https://1013399.github.io/image-2/93/1055834334_l.jpg" alt="合歡山 石門山" loading="lazy"></p><p>→到達石門山山頂<br><img src="https://1013399.github.io/image-2/93/1055837261_l.jpg" alt="合歡山 石門山" loading="lazy"></p><p>→石門山，山頂標示牌<br><img src="https://1013399.github.io/image-2/93/1055831540_l.jpg" alt="合歡山 石門山" loading="lazy"></p><p>→石門山，三等三角點No.6389<br><img src="https://1013399.github.io/image-2/93/1055837366_l.jpg" alt="合歡山 石門山" loading="lazy"></p><p>→眺望奇萊北峰與主峰<br><img src="https://1013399.github.io/image-2/93/1055839564_l.jpg" alt="合歡山 石門山" loading="lazy"></p><p>→眺望合歡東峰與主峰<br><img src="https://1013399.github.io/image-2/93/1055835126_l.jpg" alt="合歡山 石門山" loading="lazy"></p><p>→遠眺南湖大山與中央尖山<br><img src="https://1013399.github.io/image-2/93/1055836875_l.jpg" alt="合歡山 石門山" loading="lazy"></p><p>→俯瞰花蓮太魯閣方向<br><img src="https://1013399.github.io/image-2/93/1055837965_l.jpg" alt="合歡山 石門山" loading="lazy"></p><p>→眺望合歡北峰，附近有明顯的反射板<br><img src="https://1013399.github.io/image-2/93/1055836971_l.jpg" alt="合歡山 石門山" loading="lazy"></p><p>→俯瞰小風口遊客中心<br><img src="https://1013399.github.io/image-2/93/1055835236_l.jpg" alt="合歡山 石門山" loading="lazy"></p><p>→由山頂續往前行，有路徑可下往石門山第二登山口(約在台14甲34.2Km克難關附近)<br><img src="https://1013399.github.io/image-2/93/1055831543_l.jpg" alt="合歡山 石門山" loading="lazy"></p><p>→休息、賞景後，原路折返</p><p>→回到石門山登山口<br><img src="https://1013399.github.io/image-2/93/1055837266_l.jpg" alt="合歡山 石門山" loading="lazy"></p><h2 id="走走資訊" tabindex="-1"><a class="header-anchor" href="#走走資訊" aria-hidden="true">#</a> 走走資訊</h2><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>走走時間：約1小時(含休息)</p><p>高低落差：約80公尺</p></div><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2>',29),m={href:"http://tsfs.forest.gov.tw/cht/index.php?code=list&ids=31",target:"_blank",rel:"noopener noreferrer"},b=t("br",null,null,-1),_=t("img",{src:"https://1013399.github.io/image-2/93/1055838400_l.jpg",alt:"合歡山 石門山 參考地圖",loading:"lazy"},null,-1);function u(j,y){const a=p("ExternalLinkIcon");return g(),e("div",null,[n,c,o(" more "),d,t("p",null,[i("原圖連結→"),t("a",m,[i("東勢林管處遊客訂房服務中心"),r(a)]),b,_])])}const x=l(h,[["render",u],["__file","post-93-2014-11-03.html.vue"]]);export{x as default};
