import{_ as g}from"./plugin-vue_export-helper-c27b6911.js";import{r as p,o as e,c as r,a as o,b as i,e as a,d as l,f as s}from"./app-e90ae243.js";const h={},n=i("p",null,[i("strong",null,"鈺鼎步道"),a("位於台南市南化區北寮里，林務局從北寮里天艷宮後山的一條原巡山步道，開發為登山步道，委由天艷宮維護，步道全長1.6公里，擁有林相豐富的原始大自然生態，步道登頂有一觀景台，可賞日出、觀雲海、眺望南化水庫、烏山稜脈、附近村落等區域均盡收眼底，視野相當遼闊，每逢假日都吸引相當多民眾前往健行。")],-1),m={href:"http://www.tianyan.org.tw/traitem_detail.php?id=7",target:"_blank",rel:"noopener noreferrer"},d=s('<p>走走日期：2014/08/31</p><h2 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h2><p>今日參加新豐鄉鄉野健行隊活動，5部遊覽車上國道1號於下營系統交流道轉台84快速道路往玉井方向，台84路底接台20線(中山路)左轉往甲仙，經過玉井市區遇中華路右轉接台3線(與台20共線)，至北寮里Ｙ叉路直行台20線往甲仙，至台20線44.8Km玉山寶光聖堂。</p><h2 id="走走資訊" tabindex="-1"><a class="header-anchor" href="#走走資訊" aria-hidden="true">#</a> 走走資訊</h2><p>→玉山寶光聖堂，下車參觀、上廁所<br><img src="https://1013399.github.io/image-2/97/1045405837_l.jpg" alt="台南 鈺鼎步道" loading="lazy"></p><p>→續行至台20線46.3Km處左轉進入田洋農路，依指標抵達天艷宮下車<br><img src="https://1013399.github.io/image-2/97/1045407486_l.jpg" alt="台南 鈺鼎步道" loading="lazy"></p><h2 id="走走路線" tabindex="-1"><a class="header-anchor" href="#走走路線" aria-hidden="true">#</a> 走走路線</h2><p>→主祀「天艷公主」的天艷宮，仍在整建中<br><img src="https://1013399.github.io/image-2/97/1045408259_l.jpg" alt="台南 鈺鼎步道" loading="lazy"></p><p>→鈺鼎步道導覽圖<br><img src="https://1013399.github.io/image-2/97/1045408191_l.jpg" alt="台南 鈺鼎步道" loading="lazy"></p><p>→路旁長滿了開花中的含羞草<br><img src="https://1013399.github.io/image-2/97/1045407781_l.jpg" alt="台南 鈺鼎步道" loading="lazy"></p><p>→依指標右彎緩上<br><img src="https://1013399.github.io/image-2/97/1045406929_l.jpg" alt="台南 鈺鼎步道" loading="lazy"></p><p>→先走一小段水泥路<br><img src="https://1013399.github.io/image-2/97/1045409660_l.jpg" alt="台南 鈺鼎步道" loading="lazy"></p><p>→鈺鼎步道入口，旁有0K里程樁<br><img src="https://1013399.github.io/image-2/97/1045406426_l.jpg" alt="台南 鈺鼎步道" loading="lazy"></p><p>→枕木階梯與土徑步道緩上<br><img src="https://1013399.github.io/image-2/97/1045409187_l.jpg" alt="台南 鈺鼎步道" loading="lazy"></p><p>→不久即開始陡上<br><img src="https://1013399.github.io/image-2/97/1045407787_l.jpg" alt="台南 鈺鼎步道" loading="lazy"><br><img src="https://1013399.github.io/image-2/97/1045406427_l.jpg" alt="台南 鈺鼎步道" loading="lazy"><br><img src="https://1013399.github.io/image-2/97/1045408093_l.jpg" alt="台南 鈺鼎步道" loading="lazy"></p><p>→上至平緩處，經過1K里程樁<br><img src="https://1013399.github.io/image-2/97/1045408583_l.jpg" alt="台南 鈺鼎步道" loading="lazy"></p><p>→第一休息區，休息<br><img src="https://1013399.github.io/image-2/97/1045406932_l.jpg" alt="台南 鈺鼎步道" loading="lazy"></p><p>→續行，陡上<br><img src="https://1013399.github.io/image-2/97/1045405326_l.jpg" alt="台南 鈺鼎步道" loading="lazy"></p><p>→寬闊平緩地<br><img src="https://1013399.github.io/image-2/97/1045407279_l.jpg" alt="台南 鈺鼎步道" loading="lazy"></p><p>→小下坡後再陡上<br><img src="https://1013399.github.io/image-2/97/1045408585_l.jpg" alt="台南 鈺鼎步道" loading="lazy"></p><p>→第二休息區<br><img src="https://1013399.github.io/image-2/97/1045407790_l.jpg" alt="台南 鈺鼎步道" loading="lazy"></p><p>→前方山頭不知是否為今日終點涼亭<br><img src="https://1013399.github.io/image-2/97/1045409377_l.jpg" alt="台南 鈺鼎步道" loading="lazy"></p><p>→通過木欄杆鞍部<br><img src="https://1013399.github.io/image-2/97/1045408785_l.jpg" alt="台南 鈺鼎步道" loading="lazy"></p><p>→最後陡上一路到山頂涼亭(全程最陡的一段)<br><img src="https://1013399.github.io/image-2/97/1045406517_l.jpg" alt="台南 鈺鼎步道" loading="lazy"></p><p>→山頂涼亭<br><img src="https://1013399.github.io/image-2/97/1045409378_l.jpg" alt="台南 鈺鼎步道" loading="lazy"></p><p>→鈺鼎步道終點1.6K<br><img src="https://1013399.github.io/image-2/97/1045409284_l.jpg" alt="台南 鈺鼎步道" loading="lazy"></p><p>→遠眺東側的烏山稜脈<br><img src="https://1013399.github.io/image-2/97/1045407791_l.jpg" alt="台南 鈺鼎步道" loading="lazy"></p><p>→俯瞰玉山寶光聖堂<br><img src="https://1013399.github.io/image-2/97/1045408394_l.jpg" alt="台南 鈺鼎步道" loading="lazy"></p><p>→俯瞰南化水庫<br><img src="https://1013399.github.io/image-2/97/1045406518_l.jpg" alt="台南 鈺鼎步道" loading="lazy"></p><p>→眺望西側的玉井、楠西一帶<br><img src="https://1013399.github.io/image-2/97/1045408885_l.jpg" alt="台南 鈺鼎步道" loading="lazy"></p><p>→過了涼亭，續行可往糖子恩山，不在今日行程中，休息後原路折返<br><img src="https://1013399.github.io/image-2/97/1045409857_l.jpg" alt="台南 鈺鼎步道" loading="lazy"></p><p>→回到第一休息區，休息、午餐<br> →回到鈺鼎步道入口<br><img src="https://1013399.github.io/image-2/97/1045409561_l.jpg" alt="台南 鈺鼎步道" loading="lazy"></p><p>→回到天艷宮，休息、換裝後上車，前往楠西「玄空法寺」參觀<br><img src="https://1013399.github.io/image-2/97/1045405636_l.jpg" alt="台南 鈺鼎步道" loading="lazy"></p><h2 id="走走資訊-1" tabindex="-1"><a class="header-anchor" href="#走走資訊-1" aria-hidden="true">#</a> 走走資訊</h2><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>走走時間：約2小時(含午餐、休息)</p><p>高低落差：約250公尺</p></div><h2 id="參考地圖-修改過原圖" tabindex="-1"><a class="header-anchor" href="#參考地圖-修改過原圖" aria-hidden="true">#</a> 參考地圖：(修改過原圖)</h2>',36),c={href:"http://blog.xuite.net/laoweiz/blog/65418836",target:"_blank",rel:"noopener noreferrer"},b=i("br",null,null,-1),_=i("img",{src:"https://1013399.github.io/image-2/97/1045409562_l.jpg",alt:"台南 鈺鼎步道 參考地圖",loading:"lazy"},null,-1);function u(y,z){const t=p("ExternalLinkIcon");return e(),r("div",null,[n,o(" more "),i("p",null,[a("(資料摘自："),i("a",m,[a("天艷宮官方網站"),l(t)]),a(")")]),d,i("p",null,[a("原圖連結→"),i("a",c,[a("老尉子的部落格"),l(t)]),b,_])])}const x=g(h,[["render",u],["__file","post-97-2014-09-05.html.vue"]]);export{x as default};
