import{_ as g}from"./plugin-vue_export-helper-c27b6911.js";import{r as l,o as p,c as r,a as o,b as i,f as t,d as s,e}from"./app-6d4d6805.js";const h={},n=i("p",null,[i("strong",null,"稍來小雪山步道"),t("，屬雪山群峰國家步道系統，位於大雪山國家森林遊樂區內，途經鞍馬山、船型山，可連結至稍來山、鳶嘴山，步道全長約10公里，擁有眾多的扁柏、檜木、鐵杉等原始森林及豐富的植被景觀，又因位處稜脈上，有山嵐、雲海、薄霧、楓紅、晨曦、落日、繁星等多變的風景，是夏季的避暑勝地，更是一條優質的悠遊步道。")],-1),m=e('<p><strong>鞍馬山</strong>，海拔2666公尺，約位於稍來小雪山步道里程8K處，三等三角點6586號，位於軍事營區內，不易探訪。</p><p><strong>船型山</strong>，海拔2274公尺，其因峰頂頗似船型而得名，約位於步道里程2.5K處，無三角點。</p><p>走走日期：2014/08/23</p><h2 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h2><p>今日參加公司登山社活動(四部中巴)，由國道1號南下轉國道4號往豐原，接台3線左轉往東勢再接台8線，於台8線2K左轉東坑街接大雪山林道，經過大雪山林道35K收費站及43K大雪山遊客服務中心後，續行至50K小雪山莊下車。</p><h2 id="走走路線" tabindex="-1"><a class="header-anchor" href="#走走路線" aria-hidden="true">#</a> 走走路線</h2><p>→大雪山林道50K小雪山莊停車場，因故耽擱不少時間，先在此處午餐<br><img src="https://1013399.github.io/image-2/98/1044022032_l.jpg" alt="稍來小雪山步道、鞍馬山、船型山" loading="lazy"></p><p>→小雪山旅遊資訊站，設有展示室，並提供書面資訊和影片導覽<br><img src="https://1013399.github.io/image-2/98/1044022908_l.jpg" alt="稍來小雪山步道、鞍馬山、船型山" loading="lazy"></p><p>→餐畢，拍張大合照後出發，回林道再取右行<br><img src="https://1013399.github.io/image-2/98/1044022034_l.jpg" alt="稍來小雪山步道、鞍馬山、船型山" loading="lazy"></p><p>→稍來小雪山步道口，設有柵欄管制及導覽圖、指標<br><img src="https://1013399.github.io/image-2/98/1044024894_l.jpg" alt="稍來小雪山步道、鞍馬山、船型山" loading="lazy"></p><p>→水泥林道緩上<br><img src="https://1013399.github.io/image-2/98/1044025586_l.jpg" alt="稍來小雪山步道、鞍馬山、船型山" loading="lazy"></p><p>→步道9K處，每0.5K設有里程木樁<br><img src="https://1013399.github.io/image-2/98/1044024976_l.jpg" alt="稍來小雪山步道、鞍馬山、船型山" loading="lazy"></p><p>→鞍馬山，海拔2666公尺，此行最高點，三角點位於營區內，不易探訪<br><img src="https://1013399.github.io/image-2/98/1044023891_l.jpg" alt="稍來小雪山步道、鞍馬山、船型山" loading="lazy"></p><p>→過營區大門後，約5公尺叉路，取直行離開水泥路<br><img src="https://1013399.github.io/image-2/98/1044020407_l.jpg" alt="稍來小雪山步道、鞍馬山、船型山" loading="lazy"></p><p>→步道開始往下，不久經過8K里程木樁<br><img src="https://1013399.github.io/image-2/98/1044025972_l.jpg" alt="稍來小雪山步道、鞍馬山、船型山" loading="lazy"></p><p>→叉路，左往林道43K處(鞍馬山莊)，取右往收費站<br><img src="https://1013399.github.io/image-2/98/1044021352_l.jpg" alt="稍來小雪山步道、鞍馬山、船型山" loading="lazy"></p><p>→枕木步道一路往下<br><img src="https://1013399.github.io/image-2/98/1044022135_l.jpg" alt="稍來小雪山步道、鞍馬山、船型山" loading="lazy"></p><p>→聳立的台灣鐵杉<br><img src="https://1013399.github.io/image-2/98/1044024676_l.jpg" alt="稍來小雪山步道、鞍馬山、船型山" loading="lazy"></p><p>→步道繼續往下，風景優美<br><img src="https://1013399.github.io/image-2/98/1044025289_l.jpg" alt="稍來小雪山步道、鞍馬山、船型山" loading="lazy"></p><p>→沿途許多枯木，形成不同景觀<br><img src="https://1013399.github.io/image-2/98/1044022036_l.jpg" alt="稍來小雪山步道、鞍馬山、船型山" loading="lazy"></p><p>→經過步道7K<br><img src="https://1013399.github.io/image-2/98/1044025290_l.jpg" alt="稍來小雪山步道、鞍馬山、船型山" loading="lazy"></p><p>→沿途鐵杉都很漂亮<br><img src="https://1013399.github.io/image-2/98/1044025778_l.jpg" alt="稍來小雪山步道、鞍馬山、船型山" loading="lazy"></p><p>→來到步道6.7K觀景台<br><img src="https://1013399.github.io/image-2/98/1044024977_l.jpg" alt="稍來小雪山步道、鞍馬山、船型山" loading="lazy"></p><p>→觀景台視野絕佳，但已霧茫茫一片，在此休息<br><img src="https://1013399.github.io/image-2/98/1044024900_l.jpg" alt="稍來小雪山步道、鞍馬山、船型山" loading="lazy"></p><p>→觀景台旁導覽圖<br><img src="https://1013399.github.io/image-2/98/1044025595_l.jpg" alt="稍來小雪山步道、鞍馬山、船型山" loading="lazy"></p><p>→續行，緩下<br><img src="https://1013399.github.io/image-2/98/1044024978_l.jpg" alt="稍來小雪山步道、鞍馬山、船型山" loading="lazy"></p><p>→經過杜鵑林<br><img src="https://1013399.github.io/image-2/98/1044025596_l.jpg" alt="稍來小雪山步道、鞍馬山、船型山" loading="lazy"></p><p>→沿途巨木也不少<br><img src="https://1013399.github.io/image-2/98/1044021355_l.jpg" alt="稍來小雪山步道、鞍馬山、船型山" loading="lazy"></p><p>→步道繼續緩下<br><img src="https://1013399.github.io/image-2/98/1044022141_l.jpg" alt="稍來小雪山步道、鞍馬山、船型山" loading="lazy"></p><p>→步道5.2K涼亭，左側叉路可往林道41K處，在此休息<br><img src="https://1013399.github.io/image-2/98/1044024905_l.jpg" alt="稍來小雪山步道、鞍馬山、船型山" loading="lazy"></p><p>→續行，步道轉為平緩好走，經過4K<br><img src="https://1013399.github.io/image-2/98/1044021419_l.jpg" alt="稍來小雪山步道、鞍馬山、船型山" loading="lazy"></p><p>→經過步道3K後，步道開始往上<br><img src="https://1013399.github.io/image-2/98/1044022150_l.jpg" alt="稍來小雪山步道、鞍馬山、船型山" loading="lazy"></p><p>→船型山，海拔2274公尺，約在步道2.5K處，無標示也無三角點<br><img src="https://1013399.github.io/image-2/98/1044026258_l.jpg" alt="稍來小雪山步道、鞍馬山、船型山" loading="lazy"></p><p>→過了船型山後，步道往下行<br><img src="https://1013399.github.io/image-2/98/1044025469_l.jpg" alt="稍來小雪山步道、鞍馬山、船型山" loading="lazy"></p><p>→平坦空地，在此休息<br><img src="https://1013399.github.io/image-2/98/1044021829_l.jpg" alt="稍來小雪山步道、鞍馬山、船型山" loading="lazy"></p><p>→樹幹、石頭，披上一層青苔，很有原始風味<br><img src="https://1013399.github.io/image-2/98/1044021830_l.jpg" alt="稍來小雪山步道、鞍馬山、船型山" loading="lazy"></p><p>→續行緩下，到達步道1K<br><img src="https://1013399.github.io/image-2/98/1044025785_l.jpg" alt="稍來小雪山步道、鞍馬山、船型山" loading="lazy"></p><p>→步道轉為Z字形陡下<br><img src="https://1013399.github.io/image-2/98/1044025786_l.jpg" alt="稍來小雪山步道、鞍馬山、船型山" loading="lazy"></p><p>→稍來小雪山步道口，接上林道，取右行<br><img src="https://1013399.github.io/image-2/98/1044025977_l.jpg" alt="稍來小雪山步道、鞍馬山、船型山" loading="lazy"></p><p>→往回拍攝步道口<br><img src="https://1013399.github.io/image-2/98/1044022151_l.jpg" alt="稍來小雪山步道、鞍馬山、船型山" loading="lazy"></p><p>→到達大雪山森林遊樂區停車場，在此泡茶、休息，等候山友<br><img src="https://1013399.github.io/image-2/98/1044025380_l.jpg" alt="稍來小雪山步道、鞍馬山、船型山" loading="lazy"></p><p>→上車前，再拍一張合照<br><img src="https://1013399.github.io/image-2/98/1044022799_l.jpg" alt="稍來小雪山步道、鞍馬山、船型山" loading="lazy"></p><h2 id="走走資訊" tabindex="-1"><a class="header-anchor" href="#走走資訊" aria-hidden="true">#</a> 走走資訊</h2><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>走走時間：約4小時(含休息)</p><p>高低落差：約上升60公尺後下降600公尺</p></div><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2>',45),b={href:"http://recreation.forest.gov.tw/RT/RT_2_1.aspx?TR_ID=060",target:"_blank",rel:"noopener noreferrer"},c=i("br",null,null,-1),d=i("img",{src:"https://1013399.github.io/image-2/98/1044024782_l.jpg",alt:"稍來小雪山步道、鞍馬山、船型山",loading:"lazy"},null,-1);function _(u,j){const a=l("ExternalLinkIcon");return p(),r("div",null,[n,o(" more "),m,i("p",null,[t("原圖連結→"),i("a",b,[t("台灣山林悠遊網"),s(a)]),c,d])])}const K=g(h,[["render",_],["__file","post-98-2014-08-29.html.vue"]]);export{K as default};
