import{_ as l}from"./plugin-vue_export-helper-c27b6911.js";import{r as p,o,c as r,a as e,b as i,f as t,d as g,e as s}from"./app-a1659b92.js";const h={},n=i("p",null,[t("高雄市"),i("strong",null,"旗後山"),t("是旗津的最高點，高53公尺，山形宛如軍艦的艦首，可眺瞰高雄港全貌。旗後山上有兩處古蹟，「旗後燈塔」與「旗後砲台」，兩者皆為市定古蹟。")],-1),m={href:"http://snnp.cpami.gov.tw/chinese/index.php?option=com_guidemap&view=mtour&catid=4&id=52&Itemid=168",target:"_blank",rel:"noopener noreferrer"},c=s('<p><strong>旗後燈塔</strong>曾於1883年(光緒9年)興建中式方形紅磚建築的燈塔，現今已不復存在，徒留基座於今燈塔北邊。日治時期日本人為擴建高雄港，於1916年(大正5年)著手重建燈塔，1918年完工成現有之燈塔。燈塔底層為文藝復興後期的巴洛克建築，主體後方則是八角形磚塔，頂部轉為圓筒狀，目前由財政部關稅總局用心維護中。</p><p><strong>旗後砲台</strong>為光緒元年(1875年)興建的一座中西合璧砲台，並於次年完工。砲台基座為下大上橢圓，砲台分三區：北區為兵房；中區為指揮區；南區為彈藥庫，其上安置四門6.5 噸英製阿姆斯壯前膛砲。國民政府接收臺灣後，國軍於砲台及燈塔之間另築碉堡。1991年(民國80年)高雄市政府進行修復，為時3年，並於1995年1月1日開放參觀。</p><p>走走日期：2014/08/03</p><h2 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h2><p>先於西子灣租電動機車(殺價後四小時300元)，前往鼓山渡輪站搭乘觀光交通渡輪往旗津(二人加機車共50元)，到達旗津渡輪站後，走海岸路轉通山路再轉旗下巷(沿途有指標)，即可抵達旗後山登山口，於此停車。</p><h2 id="走走路線" tabindex="-1"><a class="header-anchor" href="#走走路線" aria-hidden="true">#</a> 走走路線</h2><p>→旗後山登山口，左往旗後砲台，取右往旗後燈塔<br><img src="https://1013399.github.io/image-2/99/1041291012_l.jpg" alt="旗後燈塔、旗後山、旗後砲台" loading="lazy"></p><p>→紅磚道結束，旗後燈塔入口<br><img src="https://1013399.github.io/image-2/99/1041293876_l.jpg" alt="旗後燈塔、旗後山、旗後砲台" loading="lazy"></p><p>→水泥路緩上<br><img src="https://1013399.github.io/image-2/99/1041293789_l.jpg" alt="旗後燈塔、旗後山、旗後砲台" loading="lazy"></p><p>→碉堡，上方建有觀景台，登上觀景台賞景<br><img src="https://1013399.github.io/image-2/99/1041292007_l.jpg" alt="旗後燈塔、旗後山、旗後砲台" loading="lazy"></p><p>→眺望對岸的壽山、中山大學<br><img src="https://1013399.github.io/image-2/99/1041295757_l.jpg" alt="旗後燈塔、旗後山、旗後砲台" loading="lazy"></p><p>→俯瞰高雄港、鼓山渡輪站<br><img src="https://1013399.github.io/image-2/99/1041294779_l.jpg" alt="旗後燈塔、旗後山、旗後砲台" loading="lazy"></p><p>→遠望八五大樓<br><img src="https://1013399.github.io/image-2/99/1041291409_l.jpg" alt="旗後燈塔、旗後山、旗後砲台" loading="lazy"></p><p>→續行緩上<br><img src="https://1013399.github.io/image-2/99/1041295196_l.jpg" alt="旗後燈塔、旗後山、旗後砲台" loading="lazy"></p><p>→叉路，直行水泥路往砲台，取右上階梯往燈塔<br><img src="https://1013399.github.io/image-2/99/1041295660_l.jpg" alt="旗後燈塔、旗後山、旗後砲台" loading="lazy"></p><p>→燈塔大門<br><img src="https://1013399.github.io/image-2/99/1041295367_l.jpg" alt="旗後燈塔、旗後山、旗後砲台" loading="lazy"></p><p>→旗後燈塔，巴洛克式建築<br><img src="https://1013399.github.io/image-2/99/1041292473_l.jpg" alt="旗後燈塔、旗後山、旗後砲台" loading="lazy"></p><p>→燈塔旁高點為三角點位置<br><img src="https://1013399.github.io/image-2/99/1041293306_l.jpg" alt="旗後燈塔、旗後山、旗後砲台" loading="lazy"></p><p>→旗後山，內補三等三角點No.77(銅標)<br><img src="https://1013399.github.io/image-2/99/1041295758_l.jpg" alt="旗後燈塔、旗後山、旗後砲台" loading="lazy"></p><p>→站在高點，視野更佳<br><img src="https://1013399.github.io/image-2/99/1041291200_l.jpg" alt="旗後燈塔、旗後山、旗後砲台" loading="lazy"></p><p>→壽山、中山大學、打狗英國領事館<br><img src="https://1013399.github.io/image-2/99/1041295466_l.jpg" alt="旗後燈塔、旗後山、旗後砲台" loading="lazy"></p><p>→進入燈塔內參觀，此為燈塔全景模型<br><img src="https://1013399.github.io/image-2/99/1041295661_l.jpg" alt="旗後燈塔、旗後山、旗後砲台" loading="lazy"></p><p>→正好一艘大船入港，休息後，回前一叉路往砲台<br><img src="https://1013399.github.io/image-2/99/1041291095_l.jpg" alt="旗後燈塔、旗後山、旗後砲台" loading="lazy"></p><p>→叉路，右可回燈塔，取左往砲台<br><img src="https://1013399.github.io/image-2/99/1041291201_l.jpg" alt="旗後燈塔、旗後山、旗後砲台" loading="lazy"></p><p>→叉路旁設有導覽圖<br><img src="https://1013399.github.io/image-2/99/1041295467_l.jpg" alt="旗後燈塔、旗後山、旗後砲台" loading="lazy"></p><p>→步道轉階梯緩下<br><img src="https://1013399.github.io/image-2/99/1041294092_l.jpg" alt="旗後燈塔、旗後山、旗後砲台" loading="lazy"></p><p>→旗後砲台，規模頗大<br><img src="https://1013399.github.io/image-2/99/1041292475_l.jpg" alt="旗後燈塔、旗後山、旗後砲台" loading="lazy"></p><p>→俯瞰第一港口<br><img src="https://1013399.github.io/image-2/99/1041295370_l.jpg" alt="旗後燈塔、旗後山、旗後砲台" loading="lazy"></p><p>→眺望旗津海水浴場<br><img src="https://1013399.github.io/image-2/99/1041291872_l.jpg" alt="旗後燈塔、旗後山、旗後砲台" loading="lazy"></p><p>→旗後砲台示意圖<br><img src="https://1013399.github.io/image-2/99/1041291097_l.jpg" alt="旗後燈塔、旗後山、旗後砲台" loading="lazy"></p><p>→旗後砲台解說牌<br><img src="https://1013399.github.io/image-2/99/1041291204_l.jpg" alt="旗後燈塔、旗後山、旗後砲台" loading="lazy"></p><p>→旗後砲台出入口<br><img src="https://1013399.github.io/image-2/99/1041295372_l.jpg" alt="旗後燈塔、旗後山、旗後砲台" loading="lazy"></p><p>→循步道往下行，右側有叉路可往星空隧道(鐵欄杆處)<br><img src="https://1013399.github.io/image-2/99/1041291411_l.jpg" alt="旗後燈塔、旗後山、旗後砲台" loading="lazy"></p><p>→回到旗後山登山口，取車前往中洲渡輪站附近的海濱海產店午餐<br><img src="https://1013399.github.io/image-2/99/1041294094_l.jpg" alt="旗後燈塔、旗後山、旗後砲台" loading="lazy"></p><p>→途經風車公園，停車拍照<br><img src="https://1013399.github.io/image-2/99/1041293309_l.jpg" alt="旗後燈塔、旗後山、旗後砲台" loading="lazy"></p><p>→今日海浪波濤洶湧<br><img src="https://1013399.github.io/image-2/99/1041295373_l.jpg" alt="旗後燈塔、旗後山、旗後砲台" loading="lazy"></p><p>→餐畢，回程閒逛後，回到旗津渡輪站搭船回西子灣<br><img src="https://1013399.github.io/image-2/99/1041291207_l.jpg" alt="旗後燈塔、旗後山、旗後砲台" loading="lazy"></p><h2 id="走走資訊" tabindex="-1"><a class="header-anchor" href="#走走資訊" aria-hidden="true">#</a> 走走資訊</h2><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>走走時間：旗後燈塔、旗後山、旗後砲台O型一圈約40分鐘</p><p>高低落差：約40公尺</p></div><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2>',40),d={href:"http://blog.yam.com/hwsln/article/40142522",target:"_blank",rel:"noopener noreferrer"},b=i("br",null,null,-1),_=i("img",{src:"https://1013399.github.io/image-2/99/1041293315_l.jpg",alt:"旗後燈塔、旗後山、旗後砲台",loading:"lazy"},null,-1);function u(y,j){const a=p("ExternalLinkIcon");return o(),r("div",null,[n,e(" more "),i("p",null,[t("(資料摘自："),i("a",m,[t("壽山國家自然公園"),g(a)]),t(")")]),c,i("p",null,[t("原圖連結→"),i("a",d,[t("郊外踏青去~~Blog"),g(a)]),b,_])])}const x=l(h,[["render",u],["__file","post-99-2014-08-12.html.vue"]]);export{x as default};
